import { OpenAIProvider } from './providers/openai';
import { FallbackProvider } from './providers/fallback';
import { AnalysisOptions, CompleteAnalysis } from './providers/base';
import { sleep } from '@/lib/utils';

export interface AnalysisServiceConfig {
  maxRetries: number;
  retryDelay: number;
  fallbackEnabled: boolean;
}

export class AIAnalysisService {
  private openaiProvider: OpenAIProvider;
  private fallbackProvider: FallbackProvider;
  private config: AnalysisServiceConfig;

  constructor(config: Partial<AnalysisServiceConfig> = {}) {
    this.config = {
      maxRetries: 3,
      retryDelay: 2000,
      fallbackEnabled: true,
      ...config,
    };

    const apiKey = process.env.OPENAI_API_KEY || '';
    this.openaiProvider = new OpenAIProvider(apiKey);
    this.fallbackProvider = new FallbackProvider();
  }

  async analyzeContent(
    content: string,
    options?: AnalysisOptions
  ): Promise<CompleteAnalysis> {
    let lastError: Error | null = null;

    // Try OpenAI provider with retries
    for (let attempt = 1; attempt <= this.config.maxRetries; attempt++) {
      try {
        const result = await this.openaiProvider.analyze(content, options);
        return result;
      } catch (error) {
        lastError = error as Error;
        console.warn(
          `OpenAI analysis attempt ${attempt} failed: ${error}. Retries left: ${
            this.config.maxRetries - attempt
          }`
        );

        if (attempt < this.config.maxRetries) {
          await sleep(this.config.retryDelay);
        }
      }
    }

    // If all retries failed and fallback is enabled, use fallback provider
    if (this.config.fallbackEnabled) {
      console.warn(
        'All OpenAI retries failed. Falling back to deterministic analysis.'
      );
      try {
        return await this.fallbackProvider.analyze(content, options);
      } catch (fallbackError) {
        throw new Error(
          `Both OpenAI and fallback analysis failed. Last OpenAI error: ${lastError?.message}. Fallback error: ${fallbackError}`
        );
      }
    }

    throw new Error(
      `Analysis failed after ${this.config.maxRetries} attempts. Last error: ${lastError?.message}`
    );
  }

  async healthCheck(): Promise<{
    openai: boolean;
    fallback: boolean;
  }> {
    const openaiHealth = await this.openaiProvider.healthCheck();
    const fallbackHealth = await this.fallbackProvider.healthCheck();

    return {
      openai: openaiHealth,
      fallback: fallbackHealth,
    };
  }

  getCapabilities() {
    return {
      openai: this.openaiProvider.getCapabilities(),
      fallback: this.fallbackProvider.getCapabilities(),
    };
  }
}

// Export a default instance
export const aiAnalysisService = new AIAnalysisService();

import OpenAI from 'openai';
import {
  AnalysisProvider,
  AnalysisOptions,
  CompleteAnalysis,
  CompleteAnalysisSchema,
  ProviderCapabilities,
  AnalysisError,
  ValidationError,
  RateLimitError,
  TimeoutError,
  ProviderUnavailableError,
} from './base';

export class OpenAIProvider implements AnalysisProvider {
  name = 'openai';
  private client: OpenAI;

  constructor(apiKey: string) {
    this.client = new OpenAI({
      apiKey,
      maxRetries: 3,
      timeout: 60000, // 60 seconds
    });
  }

  async analyze(
    content: string,
    options?: AnalysisOptions
  ): Promise<CompleteAnalysis> {
    const startTime = Date.now();

    try {
      const prompt = this.buildAnalysisPrompt(content, options);

      const completion = await this.client.chat.completions.create({
        model: 'gpt-4-turbo-preview',
        messages: [
          {
            role: 'system',
            content: this.getSystemPrompt(),
          },
          {
            role: 'user',
            content: prompt,
          },
        ],
        temperature: 0.1, // Low temperature for consistent results
        max_tokens: 4000,
        response_format: { type: 'json_object' },
      });

      const response = completion.choices[0]?.message?.content;
      if (!response) {
        throw new AnalysisError(
          'Empty response from OpenAI',
          'EMPTY_RESPONSE',
          this.name
        );
      }

      let parsedResponse;
      try {
        parsedResponse = JSON.parse(response);
      } catch (error) {
        throw new ValidationError(
          'Invalid JSON response from OpenAI',
          this.name
        );
      }

      // Add metadata
      const analysisResult = {
        ...parsedResponse,
        processingTime: Date.now() - startTime,
        model: 'gpt-4-turbo-preview',
        tokensUsed: completion.usage?.total_tokens || 0,
        confidence: this.calculateConfidence(parsedResponse),
      };

      // Validate the response structure
      const validatedResult = CompleteAnalysisSchema.parse(analysisResult);

      return validatedResult;
    } catch (error) {
      if (error instanceof OpenAI.APIError) {
        if (error.status === 429) {
          throw new RateLimitError(
            this.name,
            error.headers?.['retry-after'] as number
          );
        }
        if (error.status === 503 || error.status === 502) {
          throw new ProviderUnavailableError(this.name);
        }
        throw new AnalysisError(
          error.message,
          'API_ERROR',
          this.name,
          error.status
        );
      }

      if (
        error &&
        typeof error === 'object' &&
        'name' in error &&
        error.name === 'TimeoutError'
      ) {
        throw new TimeoutError(this.name, options?.timeout || 45000);
      }

      // Re-throw validation errors
      if (error instanceof ValidationError) {
        throw error;
      }

      throw new AnalysisError(
        error instanceof Error ? error.message : 'Unknown error',
        'UNKNOWN_ERROR',
        this.name
      );
    }
  }

  async healthCheck(): Promise<boolean> {
    try {
      const response = await this.client.chat.completions.create({
        model: 'gpt-3.5-turbo',
        messages: [{ role: 'user', content: 'Hello' }],
        max_tokens: 5,
      });

      return response.choices.length > 0;
    } catch (error) {
      console.error('OpenAI health check failed:', error);
      return false;
    }
  }

  getCapabilities(): ProviderCapabilities {
    return {
      maxTokens: 128000, // GPT-4 Turbo context window
      supportsStreaming: true,
      supportsBatch: false,
      rateLimit: {
        requestsPerMinute: 3500, // Tier 1 limit
        tokensPerMinute: 150000,
      },
      reliability: {
        averageUptime: 0.999,
        averageResponseTime: 2500, // 2.5 seconds
      },
    };
  }

  private getSystemPrompt(): string {
    return `You are an expert marketing analyst with deep knowledge of Simon Sinek's Golden Circle, Consumer Elements of Value, B2B Elements of Value, and CliftonStrengths frameworks.

Your task is to analyze marketing content and provide comprehensive insights across all frameworks.

You must respond with a valid JSON object that includes:
1. Golden Circle analysis (WHY, HOW, WHAT scoring and details)
2. Consumer Value Elements analysis (30 elements across 4 categories)
3. B2B Value Elements analysis (40 elements across 5 categories)
4. CliftonStrengths analysis (34 themes across 4 domains)
5. Identified barriers to growth
6. Actionable recommendations

Scoring should be on a 0-100 scale where:
- 90-100: Excellent, market-leading
- 80-89: Good, above average
- 70-79: Average, room for improvement
- 60-69: Below average, needs work
- 0-59: Poor, requires immediate attention

Be specific in your feedback and provide actionable suggestions for improvement.

Ensure all scores are realistic and justified by the content analysis.`;
  }

  private buildAnalysisPrompt(
    content: string,
    options?: AnalysisOptions
  ): string {
    let prompt = `Please analyze the following marketing content using all frameworks:\n\n"${content}"\n\n`;

    if (options) {
      if (options.contentType) {
        prompt += `Content Type: ${options.contentType}\n`;
      }
      if (options.industry) {
        prompt += `Industry: ${options.industry}\n`;
      }
      if (options.targetAudience) {
        prompt += `Target Audience: ${options.targetAudience}\n`;
      }
      if (options.conversionGoal) {
        prompt += `Conversion Goal: ${options.conversionGoal}\n`;
      }
    }

    prompt += `
Provide a comprehensive analysis in JSON format with the following structure:

{
  "goldenCircle": {
    "whyScore": number,
    "howScore": number,
    "whatScore": number,
    "overallScore": number,
    "whyDetails": {
      "clarity": number,
      "emotionalImpact": number,
      "uniqueness": number,
      "authenticity": number,
      "feedback": [string],
      "suggestions": [string]
    },
    "howDetails": { ... same structure ... },
    "whatDetails": { ... same structure ... }
  },
  "consumerValue": {
    "functionalScore": number,
    "emotionalScore": number,
    "lifeChangingScore": number,
    "socialImpactScore": number,
    "overallScore": number,
    "elementScores": { "elementName": score },
    "topElements": [string],
    "detectedElements": [...]
  },
  "b2bValue": { ... similar structure for B2B elements ... },
  "cliftonStrengths": {
    "executingScore": number,
    "influencingScore": number,
    "relationshipBuildingScore": number,
    "strategicThinkingScore": number,
    "overallScore": number,
    "themeScores": { "themeName": score },
    "topThemes": [string],
    "detectedThemes": [...]
  },
  "barriers": {
    "barriers": [
      {
        "name": string,
        "description": string,
        "category": string,
        "severity": "LOW"|"MEDIUM"|"HIGH"|"CRITICAL",
        "impact": string,
        "solution": string
      }
    ]
  },
  "recommendations": {
    "recommendations": [
      {
        "title": string,
        "description": string,
        "category": string,
        "priority": "LOW"|"MEDIUM"|"HIGH"|"CRITICAL",
        "impact": string,
        "effort": string,
        "timeframe": string
      }
    ]
  }
}`;

    return prompt;
  }

  private calculateConfidence(analysis: Record<string, unknown>): number {
    // Calculate confidence based on analysis completeness and score consistency
    let confidence = 0.8; // Base confidence

    // Check if all required sections are present
    const requiredSections = [
      'goldenCircle',
      'consumerValue',
      'b2bValue',
      'cliftonStrengths',
      'barriers',
      'recommendations',
    ];
    const presentSections = requiredSections.filter(
      (section) => analysis[section]
    );

    confidence *= presentSections.length / requiredSections.length;

    // Check for reasonable score distributions
    const scores = [
      (analysis.goldenCircle as { overallScore?: number })?.overallScore,
      (analysis.consumerValue as { overallScore?: number })?.overallScore,
      (analysis.b2bValue as { overallScore?: number })?.overallScore,
      (analysis.cliftonStrengths as { overallScore?: number })?.overallScore,
    ].filter((score): score is number => typeof score === 'number');

    if (scores.length > 0) {
      const avgScore = scores.reduce((a, b) => a + b, 0) / scores.length;
      const variance =
        scores.reduce((acc, score) => acc + Math.pow(score - avgScore, 2), 0) /
        scores.length;

      // Lower confidence if scores are too uniform (likely hallucinated)
      if (variance < 10) {
        confidence *= 0.9;
      }
    }

    // Check for presence of detailed feedback
    if (
      (analysis.barriers as { barriers?: unknown[] })?.barriers?.length > 0 &&
      (analysis.recommendations as { recommendations?: unknown[] })?.recommendations?.length > 0
    ) {
      confidence *= 1.1;
    }

    return Math.min(confidence, 1);
  }
}

import { prisma } from '@/lib/prisma';
import { ProductionContentExtractor } from '@/lib/production-content-extractor';
import { NextRequest, NextResponse } from 'next/server';

export const maxDuration = 300; // 5 minutes for complete Phase 1

export async function POST(request: NextRequest) {
  try {
    const body = await request.json();
    const { url } = body;

    if (!url) {
      return NextResponse.json({
        success: false,
        error: 'URL is required'
      }, { status: 400 });
    }

    console.log(`ðŸš€ Starting Complete Phase 1 analysis for: ${url}`);

    // Step 1: Content Scraping
    console.log('ðŸ“Š Step 1: Scraping website content...');
    const extractor = new ProductionContentExtractor();
    const scrapedContent = await extractor.extractContent(url);

    // Step 2: Google SEO Tools Analysis
    console.log('ðŸ” Step 2: Running Google SEO Tools analysis...');
    const seoAnalysis = await runGoogleSEOTools(url, scrapedContent);

    // Step 3: Lighthouse Performance Test
    console.log('ðŸ—ï¸ Step 3: Running Lighthouse performance test...');
    const lighthouseData = await runLighthouseAnalysis(url);

    // Step 4: ESLint/QA Tools Analysis
    console.log('ðŸ”§ Step 4: Running QA tools analysis...');
    const qaAnalysis = await runQAAnalysis(url, scrapedContent);

    // Step 5: Google Trends Analysis
    console.log('ðŸ“ˆ Step 5: Running Google Trends analysis...');
    const trendsAnalysis = await runGoogleTrendsAnalysis(scrapedContent);

    // Step 6: Competition Analysis
    console.log('ðŸ† Step 6: Running competition analysis...');
    const competitionAnalysis = await runCompetitionAnalysis(url, scrapedContent);

    const phase1Result = {
      url,
      timestamp: new Date().toISOString(),
      scrapedContent,
      seoAnalysis,
      lighthouseData,
      qaAnalysis,
      trendsAnalysis,
      competitionAnalysis,
      summary: {
        contentWords: scrapedContent.wordCount || 0,
        contentImages: scrapedContent.imageCount || 0,
        contentLinks: scrapedContent.linkCount || 0,
        seoScore: seoAnalysis.overallScore || 0,
        performanceScore: lighthouseData?.scores?.performance || 0,
        accessibilityScore: lighthouseData?.scores?.accessibility || 0,
        qaIssues: qaAnalysis.issues?.length || 0,
        trendsData: trendsAnalysis.trendingTopics?.length || 0,
        competitors: competitionAnalysis.competitors?.length || 0
      }
    };

    console.log(`âœ… Complete Phase 1 analysis completed for: ${url}`);

    // Store Phase 1 results in database
    const analysisId = `phase1-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;

    try {
      // Store in main Analysis table
      await prisma.analysis.create({
        data: {
          id: analysisId,
          content: JSON.stringify(phase1Result),
          contentType: 'phase1-complete',
          status: 'COMPLETED',
          score: phase1Result.summary.seoScore
        }
      });

      // Store individual SEO analysis if available
      if (seoAnalysis.overallScore > 0) {
        await prisma.seo_analyses.create({
          data: {
            analysis_id: analysisId,
            overall_seo_score: seoAnalysis.overallScore,
            technical_seo_score: seoAnalysis.overallScore * 0.8,
            content_quality_score: seoAnalysis.overallScore * 0.9,
            keyword_optimization_score: seoAnalysis.overallScore * 0.7,
            backlink_score: 0
          }
        });
      }

      console.log(`ðŸ“„ Phase 1 results stored in database: ${analysisId}`);
    } catch (dbError) {
      console.error('Failed to store Phase 1 results in database:', dbError);
      // Continue with response even if database storage fails
    }

    return NextResponse.json({
      success: true,
      url,
      phase: 1,
      analysisId,
      data: phase1Result,
      message: 'Complete Phase 1 analysis completed successfully'
    });

  } catch (error) {
    console.error('Complete Phase 1 analysis error:', error);
    return NextResponse.json({
      success: false,
      error: 'Complete Phase 1 analysis failed',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 });
  }
}

// Google SEO Tools Analysis
async function runGoogleSEOTools(url: string, content: any) {
  try {
    // Extract keywords from content
    const keywords = extractKeywords(content);

    // Analyze meta tags
    const metaAnalysis = analyzeMetaTags(content);

    // Check for structured data
    const structuredData = analyzeStructuredData(content);

    // Analyze headings structure
    const headingAnalysis = analyzeHeadings(content);

    // Check for internal/external links
    const linkAnalysis = analyzeLinks(content);

    // Calculate overall SEO score
    const overallScore = calculateSEOScore(metaAnalysis, headingAnalysis, linkAnalysis, structuredData);

    return {
      keywords,
      metaAnalysis,
      structuredData,
      headingAnalysis,
      linkAnalysis,
      overallScore,
      recommendations: generateSEORecommendations(metaAnalysis, headingAnalysis, linkAnalysis, structuredData)
    };
  } catch (error) {
    console.error('Google SEO Tools analysis failed:', error);
    return { error: 'SEO analysis failed', overallScore: 0 };
  }
}

// Lighthouse Performance Analysis
async function runLighthouseAnalysis(url: string) {
  try {
    // Check if Google API key is available
    if (!process.env.GOOGLE_API_KEY) {
      console.log('âš ï¸ Google API key not found, using mock Lighthouse data');
      return {
        scores: {
          performance: 75,
          accessibility: 80,
          bestPractices: 85,
          seo: 70
        },
        metrics: {
          firstContentfulPaint: 1500,
          largestContentfulPaint: 2500,
          cumulativeLayoutShift: 0.1,
          speedIndex: 2000
        },
        opportunities: [
          { id: 'unused-css-rules', title: 'Remove unused CSS', description: 'Remove unused CSS rules', score: 0.8 },
          { id: 'unused-javascript', title: 'Remove unused JavaScript', description: 'Remove unused JavaScript code', score: 0.7 }
        ],
        note: 'Mock data - configure GOOGLE_API_KEY for real Lighthouse analysis'
      };
    }

    // Use Google PageSpeed Insights API
    const response = await fetch(`https://www.googleapis.com/pagespeedonline/v5/runPagespeed?url=${encodeURIComponent(url)}&key=${process.env.GOOGLE_API_KEY}`);

    if (!response.ok) {
      throw new Error(`Lighthouse API failed: ${response.status} ${response.statusText}`);
    }

    const data = await response.json();

    return {
      scores: {
        performance: data.lighthouseResult.categories.performance.score * 100,
        accessibility: data.lighthouseResult.categories.accessibility.score * 100,
        bestPractices: data.lighthouseResult.categories['best-practices'].score * 100,
        seo: data.lighthouseResult.categories.seo.score * 100
      },
      metrics: {
        firstContentfulPaint: data.lighthouseResult.audits['first-contentful-paint'].numericValue,
        largestContentfulPaint: data.lighthouseResult.audits['largest-contentful-paint'].numericValue,
        cumulativeLayoutShift: data.lighthouseResult.audits['cumulative-layout-shift'].numericValue,
        speedIndex: data.lighthouseResult.audits['speed-index'].numericValue
      },
      opportunities: data.lighthouseResult.categories.performance.auditRefs
        .filter((audit: any) => audit.group === 'load-opportunities')
        .map((audit: any) => ({
          id: audit.id,
          title: audit.title,
          description: audit.description,
          score: audit.score
        })),
      diagnostics: data.lighthouseResult.categories.performance.auditRefs
        .filter((audit: any) => audit.group === 'diagnostics')
        .map((audit: any) => ({
          id: audit.id,
          title: audit.title,
          description: audit.description,
          score: audit.score
        }))
    };
  } catch (error) {
    console.error('Lighthouse analysis failed:', error);
    return {
      error: 'Lighthouse analysis failed',
      scores: { performance: 0, accessibility: 0, bestPractices: 0, seo: 0 },
      note: 'Lighthouse analysis unavailable - check API configuration'
    };
  }
}

// QA Tools Analysis
async function runQAAnalysis(url: string, content: any) {
  try {
    const issues = [];

    // Check for common issues
    if (!content.title) issues.push({ type: 'critical', message: 'Missing page title' });
    if (!content.metaDescription) issues.push({ type: 'warning', message: 'Missing meta description' });
    if (content.wordCount < 300) issues.push({ type: 'warning', message: 'Content too short (less than 300 words)' });
    if (content.imageCount === 0) issues.push({ type: 'info', message: 'No images found' });
    if (content.linkCount === 0) issues.push({ type: 'warning', message: 'No internal links found' });

    // Check for broken elements
    if (content.brokenLinks && content.brokenLinks.length > 0) {
      issues.push({ type: 'error', message: `Found ${content.brokenLinks.length} broken links` });
    }

    // Check for accessibility issues
    if (!content.headings?.h1 || content.headings.h1.length === 0) {
      issues.push({ type: 'error', message: 'Missing H1 heading' });
    }

    return {
      issues,
      score: Math.max(0, 100 - (issues.filter(i => i.type === 'error').length * 20) - (issues.filter(i => i.type === 'warning').length * 10)),
      recommendations: generateQARecommendations(issues)
    };
  } catch (error) {
    console.error('QA analysis failed:', error);
    return { error: 'QA analysis failed', issues: [], score: 0 };
  }
}

// Google Trends Analysis
async function runGoogleTrendsAnalysis(content: any) {
  try {
    const keywords = extractKeywords(content);
    const trendingTopics = [];

    // Analyze trending topics for each keyword
    for (const keyword of keywords.slice(0, 5)) { // Limit to top 5 keywords
      try {
        const trendsData = await analyzeTrendingTopics(keyword);
        trendingTopics.push({
          keyword,
          trend: trendsData.trend,
          relatedQueries: trendsData.relatedQueries,
          risingQueries: trendsData.risingQueries
        });
      } catch (err) {
        console.error(`Trends analysis failed for keyword: ${keyword}`, err);
      }
    }

    return {
      trendingTopics,
      overallTrend: calculateOverallTrend(trendingTopics),
      recommendations: generateTrendsRecommendations(trendingTopics)
    };
  } catch (error) {
    console.error('Google Trends analysis failed:', error);
    return { error: 'Trends analysis failed', trendingTopics: [] };
  }
}

// Competition Analysis
async function runCompetitionAnalysis(url: string, content: any) {
  try {
    const domain = new URL(url).hostname;
    const competitors = await findCompetitors(domain, content);

    return {
      competitors,
      competitiveAnalysis: await analyzeCompetitors(competitors),
      recommendations: generateCompetitionRecommendations(competitors)
    };
  } catch (error) {
    console.error('Competition analysis failed:', error);
    return { error: 'Competition analysis failed', competitors: [] };
  }
}

// Helper functions
function extractKeywords(content: any): string[] {
  const text = content.cleanText || content.content || '';
  const words = text.toLowerCase()
    .replace(/[^\w\s]/g, '')
    .split(/\s+/)
    .filter(word => word.length > 3);

  const wordCount: { [key: string]: number } = {};
  words.forEach(word => {
    wordCount[word] = (wordCount[word] || 0) + 1;
  });

  return Object.entries(wordCount)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 20)
    .map(([word]) => word);
}

function analyzeMetaTags(content: any) {
  return {
    title: {
      present: !!content.title,
      length: content.title?.length || 0,
      score: content.title ? Math.min(10, Math.max(0, 10 - Math.abs(60 - content.title.length))) : 0
    },
    description: {
      present: !!content.metaDescription,
      length: content.metaDescription?.length || 0,
      score: content.metaDescription ? Math.min(10, Math.max(0, 10 - Math.abs(160 - content.metaDescription.length))) : 0
    },
    keywords: {
      present: !!content.metaKeywords,
      count: content.metaKeywords?.length || 0,
      score: content.metaKeywords ? Math.min(10, content.metaKeywords.length) : 0
    }
  };
}

function analyzeStructuredData(content: any) {
  return {
    present: content.structuredData && content.structuredData.length > 0,
    count: content.structuredData?.length || 0,
    types: content.structuredData?.map((item: any) => item.type) || [],
    score: content.structuredData ? Math.min(10, content.structuredData.length) : 0
  };
}

function analyzeHeadings(content: any) {
  const headings = content.headings || {};
  return {
    h1: {
      present: headings.h1 && headings.h1.length > 0,
      count: headings.h1?.length || 0,
      score: headings.h1 ? Math.min(10, headings.h1.length) : 0
    },
    h2: {
      present: headings.h2 && headings.h2.length > 0,
      count: headings.h2?.length || 0,
      score: headings.h2 ? Math.min(10, headings.h2.length) : 0
    },
    h3: {
      present: headings.h3 && headings.h3.length > 0,
      count: headings.h3?.length || 0,
      score: headings.h3 ? Math.min(10, headings.h3.length) : 0
    }
  };
}

function analyzeLinks(content: any) {
  return {
    internal: content.internalLinks || 0,
    external: content.externalLinks || 0,
    broken: content.brokenLinks?.length || 0,
    score: Math.max(0, 10 - (content.brokenLinks?.length || 0))
  };
}

function calculateSEOScore(meta: any, headings: any, links: any, structured: any) {
  const scores = [
    meta.title.score,
    meta.description.score,
    meta.keywords.score,
    headings.h1.score,
    headings.h2.score,
    headings.h3.score,
    links.score,
    structured.score
  ];

  return Math.round(scores.reduce((a, b) => a + b, 0) / scores.length);
}

function generateSEORecommendations(meta: any, headings: any, links: any, structured: any) {
  const recommendations = [];

  if (meta.title.score < 5) recommendations.push('Improve page title length and relevance');
  if (meta.description.score < 5) recommendations.push('Add or improve meta description');
  if (headings.h1.score === 0) recommendations.push('Add H1 heading');
  if (headings.h2.score < 3) recommendations.push('Add more H2 headings for structure');
  if (links.score < 5) recommendations.push('Fix broken links');
  if (structured.score === 0) recommendations.push('Add structured data markup');

  return recommendations;
}

function generateQARecommendations(issues: any[]) {
  return issues.map(issue => ({
    priority: issue.type === 'error' ? 'high' : issue.type === 'warning' ? 'medium' : 'low',
    action: issue.message,
    impact: issue.type === 'error' ? 'Critical' : issue.type === 'warning' ? 'Important' : 'Nice to have'
  }));
}

