function convertMarkdownToHTML(markdown: string): string {
  // Basic markdown to HTML conversion for better formatting
  const html = markdown
    // Headers
    .replace(/^# (.*$)/gim, '<h1>$1</h1>')
    .replace(/^## (.*$)/gim, '<h2>$1</h2>')
    .replace(/^### (.*$)/gim, '<h3>$1</h3>')
    .replace(/^#### (.*$)/gim, '<h4>$1</h4>')
    .replace(/^##### (.*$)/gim, '<h5>$1</h5>')
    .replace(/^###### (.*$)/gim, '<h6>$1</h6>')

    // Bold and italic
    .replace(/\*\*(.*?)\*\*/g, '<strong>$1</strong>')
    .replace(/\*(.*?)\*/g, '<em>$1</em>')

    // Code blocks
    .replace(/```([\s\S]*?)```/g, '<pre><code>$1</code></pre>')
    .replace(/`(.*?)`/g, '<code>$1</code>')

    // Lists
    .replace(/^- (.*$)/gim, '<li>$1</li>')
    .replace(/^\* (.*$)/gim, '<li>$1</li>')
    .replace(/^\d+\. (.*$)/gim, '<li>$1</li>')

    // Line breaks
    .replace(/\n\n/g, '</p><p>')
    .replace(/\n/g, '<br>')

    // Horizontal rules
    .replace(/^---$/gim, '<hr>')
    .replace(/^___$/gim, '<hr>')
    .replace(/^\*\*\*$/gim, '<hr>');

  // Wrap in proper HTML structure
  return `
    <!DOCTYPE html>
    <html lang="en">
    <head>
      <meta charset="UTF-8">
      <meta name="viewport" content="width=device-width, initial-scale=1.0">
      <title>Website Evaluation Worksheet Integration Guide</title>
      <style>
        body {
          font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
          line-height: 1.6;
          max-width: 1200px;
          margin: 0 auto;
          padding: 20px;
          color: #333;
          background: #fff;
        }
        h1 { color: #2563eb; border-bottom: 3px solid #2563eb; padding-bottom: 10px; }
        h2 { color: #1e40af; border-bottom: 2px solid #e5e7eb; padding-bottom: 8px; margin-top: 30px; }
        h3 { color: #1d4ed8; margin-top: 25px; }
        h4 { color: #2563eb; margin-top: 20px; }
        pre { background: #f3f4f6; padding: 15px; border-radius: 8px; overflow-x: auto; border-left: 4px solid #2563eb; }
        code { background: #f3f4f6; padding: 2px 6px; border-radius: 4px; font-family: 'Monaco', 'Menlo', monospace; }
        table { width: 100%; border-collapse: collapse; margin: 20px 0; }
        th, td { border: 1px solid #d1d5db; padding: 12px; text-align: left; }
        th { background: #f9fafb; font-weight: 600; color: #374151; }
        tr:nth-child(even) { background: #f9fafb; }
        .highlight { background: #fef3c7; padding: 2px 4px; border-radius: 4px; }
        .success { color: #059669; font-weight: 600; }
        .warning { color: #d97706; font-weight: 600; }
        .error { color: #dc2626; font-weight: 600; }
        hr { border: none; border-top: 2px solid #e5e7eb; margin: 30px 0; }
        ul, ol { padding-left: 20px; }
        li { margin: 8px 0; }
        .toc { background: #f8fafc; padding: 20px; border-radius: 8px; margin: 20px 0; border-left: 4px solid #2563eb; }
        .metadata { background: #f0f9ff; padding: 15px; border-radius: 8px; margin: 20px 0; border: 1px solid #0ea5e9; }
        @media print {
          body { max-width: none; margin: 0; padding: 15px; }
          h1, h2, h3, h4, h5, h6 { page-break-after: avoid; }
          pre, table { page-break-inside: avoid; }
        }
      </style>
    </head>
    <body>
      <div class="metadata">
        <h3>ðŸ“‹ Document Information</h3>
        <p><strong>Title:</strong> Website Evaluation Worksheet Integration Guide</p>
        <p><strong>Description:</strong> Complete guide showing how the system captures actual language and evidence matching</p>
        <p><strong>Generated:</strong> ${new Date().toLocaleDateString()} at ${new Date().toLocaleTimeString()}</p>
        <p><strong>Version:</strong> 1.0.0</p>
      </div>

      <div>${html}</div>

      <hr>
      <div class="metadata">
        <p><em>This document was generated by the Zero Barriers Growth Accelerator system. It provides complete transparency on how website evaluation scores are calculated with specific evidence and exact language extraction.</em></p>
      </div>
    </body>
    </html>
  `;
}

import { NextRequest, NextResponse } from 'next/server';
import { ExecutiveReportGenerator } from '@/lib/executive-report-generator';
import { analyzeWithGemini } from '@/lib/free-ai-analysis';
import { extractWithProduction } from '@/lib/production-content-extractor';
import { runLighthouseAnalysis } from '@/lib/lighthouse-service';
import { spawn } from 'child_process';
import path from 'path';

export async function POST(request: NextRequest) {
  try {
    const { url } = await request.json();

    if (!url) {
      return NextResponse.json({
        success: false,
        error: 'URL is required'
      }, { status: 400 });
    }

    console.log(`Generating executive report for: ${url}`);

    // Step 1: Scrape website content
    console.log('Step 1: Scraping website content...');
    const scrapedContent = await extractWithProduction(url);

    // Step 2: Run Lighthouse analysis
    console.log('Step 2: Running Lighthouse analysis...');
    const lighthouseData = await runLighthouseAnalysis(url);

    // Step 3: Run PageAudit analysis
    console.log('Step 3: Running PageAudit analysis...');
    const pageAuditData = await new Promise((resolve, reject) => {
      const scriptPath = path.join(process.cwd(), 'scripts', 'pageaudit-analysis.js');
      const child = spawn('node', [scriptPath, url]);

      let output = '';
      let errorOutput = '';

      child.stdout.on('data', (data) => {
        output += data.toString();
      });

      child.stderr.on('data', (data) => {
        errorOutput += data.toString();
      });

      child.on('close', (code) => {
        if (code === 0) {
          try {
            const result = JSON.parse(output);
            resolve(result);
          } catch (parseError) {
            console.error('Failed to parse PageAudit output:', parseError);
            resolve({ error: 'Failed to parse PageAudit results' });
          }
        } else {
          console.error('PageAudit script failed:', errorOutput);
          resolve({ error: 'PageAudit analysis failed' });
        }
      });
    });

    // Step 4: Run AI analysis with scraped data
    console.log('Step 4: Running AI analysis...');
    const aiAnalysis = await analyzeWithGemini(scrapedContent.content, 'comprehensive');

    // Step 5: Combine all analysis data
    const comprehensiveAnalysis = {
      url,
      timestamp: new Date().toISOString(),
      ...aiAnalysis,
      lighthouseAnalysis: lighthouseData,
      pageAuditAnalysis: pageAuditData,
      scrapedContent: {
        content: scrapedContent.content,
        title: scrapedContent.title,
        metaDescription: scrapedContent.metaDescription,
        wordCount: scrapedContent.wordCount,
        technicalInfo: scrapedContent.technicalInfo
      }
    };

    // Step 6: Generate executive report
    console.log('Step 6: Generating executive report...');
    const reportGenerator = new ExecutiveReportGenerator();
    const markdownReport = reportGenerator.generateMarkdownReport(comprehensiveAnalysis);
    const htmlReport = reportGenerator.generateHtmlReport(markdownReport);

    // Step 7: Save report to file system
    const reportId = `executive-report-${Date.now()}`;
    const reportPath = path.join(process.cwd(), 'reports', `${reportId}.md`);
    const htmlReportPath = path.join(process.cwd(), 'reports', `${reportId}.html`);

    // Ensure reports directory exists
    const fs = require('fs');
    const reportsDir = path.join(process.cwd(), 'reports');
    if (!fs.existsSync(reportsDir)) {
      fs.mkdirSync(reportsDir, { recursive: true });
    }

    // Write markdown report
    fs.writeFileSync(reportPath, markdownReport);

    // Write HTML report
    fs.writeFileSync(htmlReportPath, htmlReport);

    // Step 8: Return comprehensive response
    return NextResponse.json({
      success: true,
      reportId,
      reportPath,
      htmlReportPath,
      markdownContent: markdownReport,
      htmlContent: htmlReport,
      analysis: comprehensiveAnalysis,
      metadata: {
        url,
        generatedAt: new Date().toISOString(),
        reportId,
        frameworks: [
          'Golden Circle Analysis',
          'Elements of Value',
          'B2B Elements',
          'CliftonStrengths',
          'Lighthouse Performance',
          'PageAudit Analysis',
          'SEO Analysis'
        ],
        dataSources: [
          'Website Content Scraping',
          'Lighthouse Performance Audit',
          'PageAudit Technical Analysis',
          'Google Gemini AI Analysis',
          'Comprehensive Framework Analysis'
        ]
      }
    });

  } catch (error) {
    console.error('Failed to generate executive report:', error);
    return NextResponse.json({
      success: false,
      error: 'Failed to generate executive report',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 });
  }
}

export async function GET(request: NextRequest) {
  try {
    const { searchParams } = new URL(request.url);
    const reportId = searchParams.get('reportId');

    if (!reportId) {
      // List all available reports
      const fs = require('fs');
      const path = require('path');
      const reportsDir = path.join(process.cwd(), 'reports');

      if (!fs.existsSync(reportsDir)) {
        return NextResponse.json({
          success: true,
          reports: []
        });
      }

      const files = fs.readdirSync(reportsDir);
      const reports = files
        .filter((file: string) => file.endsWith('.md'))
        .map((file: string) => {
          const reportId = file.replace('.md', '');
          const stats = fs.statSync(path.join(reportsDir, file));
          return {
            reportId,
            filename: file,
            createdAt: stats.birthtime,
            size: stats.size
          };
        })
        .sort((a: any, b: any) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());

      return NextResponse.json({
        success: true,
        reports
      });
    }

    // Get specific report
    const fs = require('fs');
    const path = require('path');
    const reportPath = path.join(process.cwd(), 'reports', `${reportId}.md`);
    const htmlReportPath = path.join(process.cwd(), 'reports', `${reportId}.html`);

    if (!fs.existsSync(reportPath)) {
      return NextResponse.json({
        success: false,
        error: 'Report not found'
      }, { status: 404 });
    }

    const markdownContent = fs.readFileSync(reportPath, 'utf8');
    const htmlContent = fs.existsSync(htmlReportPath) 
      ? fs.readFileSync(htmlReportPath, 'utf8') 
      : null;

    const stats = fs.statSync(reportPath);

    return NextResponse.json({
      success: true,
      reportId,
      markdownContent,
      htmlContent,
      metadata: {
        createdAt: stats.birthtime,
        size: stats.size,
        lastModified: stats.mtime
      }
    });

  } catch (error) {
    console.error('Failed to retrieve report:', error);
    return NextResponse.json({
      success: false,
      error: 'Failed to retrieve report',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 });
  }
}

