'use client';

import GoldenCircleAssessment from '@/components/assessments/GoldenCircleAssessment';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import {
    AlertCircle,
    BarChart3,
    Building2,
    CheckCircle,
    Download,
    FileText,
    Loader2,
    Target,
    Users
} from 'lucide-react';
import { useEffect, useState } from 'react';

interface AnalysisData {
  url: string;
  content: any;
  goldenCircle?: any;
  elementsOfValue?: any;
  cliftonStrengths?: any;
  b2bElements?: any;
  comprehensive?: any;
}

export default function AnalysisPage() {
  const [url, setUrl] = useState('');
  const [content, setContent] = useState<any>(null);
  const [analysisData, setAnalysisData] = useState<AnalysisData | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Load data from localStorage on mount
  useEffect(() => {
    const savedData = localStorage.getItem('analysisData');
    if (savedData) {
      try {
        const parsed = JSON.parse(savedData);
        setAnalysisData(parsed);
        setUrl(parsed.url || '');
        setContent(parsed.content || null);
      } catch (err) {
        console.error('Failed to parse saved analysis data:', err);
      }
    }
  }, []);

  const runPhase1 = async () => {
    if (!url) {
      setError('Please enter a URL');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/analyze/phase1-simple', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url }),
      });

      const data = await response.json();

      if (data.success) {
        setContent(data.data);
        setAnalysisData(prev => ({
          ...prev,
          url,
          content: data.data
        }));

        // Save to localStorage
        const newData = { url, content: data.data };
        localStorage.setItem('analysisData', JSON.stringify(newData));
      } else {
        setError(data.error || 'Phase 1 failed');
      }
    } catch (err) {
      setError('Network error occurred');
    } finally {
      setIsLoading(false);
    }
  };

  const runComprehensiveAnalysis = async () => {
    if (!content) {
      setError('Please run Phase 1 first');
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/analyze/comprehensive-report', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url, content }),
      });

      const data = await response.json();

      if (data.success) {
        setAnalysisData(prev => ({
          ...prev,
          ...data.data
        }));

        // Save to localStorage
        localStorage.setItem('analysisData', JSON.stringify(data.data));
      } else {
        setError(data.error || 'Comprehensive analysis failed');
      }
    } catch (err) {
      setError('Network error occurred');
    } finally {
      setIsLoading(false);
    }
  };

  const downloadReport = () => {
    if (!analysisData) return;

    const reportData = {
      url: analysisData.url,
      timestamp: new Date().toISOString(),
      assessments: analysisData
    };

    const blob = new Blob([JSON.stringify(reportData, null, 2)], { type: 'application/json' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `analysis-report-${analysisData.url.replace(/[^a-zA-Z0-9]/g, '-')}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const getAssessmentStatus = (assessment: any) => {
    if (!assessment) return { status: 'pending', icon: AlertCircle, color: 'text-gray-500' };
    if (assessment.success) return { status: 'completed', icon: CheckCircle, color: 'text-green-500' };
    return { status: 'failed', icon: AlertCircle, color: 'text-red-500' };
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 dark:from-slate-900 dark:to-slate-800">
      <div className="container mx-auto py-8 px-4">

        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-4">
            Website Analysis Dashboard
          </h1>
          <p className="text-xl text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
            Comprehensive business framework analysis with separate assessment views
          </p>
        </div>

        {/* URL Input */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle>Analysis Setup</CardTitle>
            <CardDescription>
              Enter a website URL to begin the analysis process
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="flex gap-4">
              <input
                id="analysis-url"
                name="analysis-url"
                type="url"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                placeholder="https://example.com"
                className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                aria-label="Enter website URL for analysis"
              />
              <Button
                onClick={runPhase1}
                disabled={isLoading || !url}
                className="px-6"
              >
                {isLoading ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Analyzing...
                  </>
                ) : (
                  'Run Phase 1'
                )}
              </Button>
            </div>
            {error && (
              <div className="mt-4 p-3 bg-red-50 border border-red-200 rounded flex items-center gap-2">
                <AlertCircle className="h-4 w-4 text-red-500" />
                <span className="text-sm text-red-700">{error}</span>
              </div>
            )}
          </CardContent>
        </Card>

        {/* Content Preview */}
        {content && (
          <Card className="mb-8">
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <FileText className="h-5 w-5" />
                Content Preview
              </CardTitle>
              <CardDescription>
                Scraped content from {url}
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="max-h-64 overflow-y-auto bg-gray-50 p-4 rounded">
                <pre className="text-sm whitespace-pre-wrap">
                  {content.cleanText || content.content || 'No content available'}
                </pre>
              </div>
            </CardContent>
          </Card>
        )}

        {/* Assessment Tabs */}
        {content && (
          <Tabs defaultValue="golden-circle" className="w-full">
            <TabsList className="grid w-full grid-cols-5">
              <TabsTrigger value="golden-circle" className="flex items-center gap-2">
                <Target className="h-4 w-4" />
                Golden Circle
                {(() => {
                  const status = getAssessmentStatus(analysisData?.goldenCircle);
                  return <status.icon className={`h-3 w-3 ${status.color}`} />;
                })()}
              </TabsTrigger>
              <TabsTrigger value="elements-of-value" className="flex items-center gap-2">
                <BarChart3 className="h-4 w-4" />
                Elements of Value
                {(() => {
                  const status = getAssessmentStatus(analysisData?.elementsOfValue);
                  return <status.icon className={`h-3 w-3 ${status.color}`} />;
                })()}
              </TabsTrigger>
              <TabsTrigger value="clifton-strengths" className="flex items-center gap-2">
                <Users className="h-4 w-4" />
                CliftonStrengths
                {(() => {
                  const status = getAssessmentStatus(analysisData?.cliftonStrengths);
                  return <status.icon className={`h-3 w-3 ${status.color}`} />;
                })()}
              </TabsTrigger>
              <TabsTrigger value="b2b-elements" className="flex items-center gap-2">
                <Building2 className="h-4 w-4" />
                B2B Elements
                {(() => {
                  const status = getAssessmentStatus(analysisData?.b2bElements);
                  return <status.icon className={`h-3 w-3 ${status.color}`} />;
                })()}
              </TabsTrigger>
              <TabsTrigger value="comprehensive" className="flex items-center gap-2">
                <FileText className="h-4 w-4" />
                Comprehensive
                {(() => {
                  const status = getAssessmentStatus(analysisData?.comprehensive);
                  return <status.icon className={`h-3 w-3 ${status.color}`} />;
                })()}
              </TabsTrigger>
            </TabsList>

            <TabsContent value="golden-circle" className="mt-6">
              <GoldenCircleAssessment
                url={url}
                content={content}
                onComplete={(data) => {
                  setAnalysisData(prev => ({
                    ...prev,
                    goldenCircle: { success: true, data, assessment: 'Golden Circle' }
                  }));
                }}
              />
            </TabsContent>

            <TabsContent value="elements-of-value" className="mt-6">
              <Card>
                <CardHeader>
                  <CardTitle>Elements of Value Assessment</CardTitle>
                  <CardDescription>
                    Coming Soon - Individual assessment component
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <Button disabled className="w-full">
                    <BarChart3 className="mr-2 h-4 w-4" />
                    Elements of Value Analysis
                  </Button>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="clifton-strengths" className="mt-6">
              <Card>
                <CardHeader>
                  <CardTitle>CliftonStrengths Assessment</CardTitle>
                  <CardDescription>
                    Coming Soon - Individual assessment component
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <Button disabled className="w-full">
                    <Users className="mr-2 h-4 w-4" />
                    CliftonStrengths Analysis
                  </Button>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="b2b-elements" className="mt-6">
              <Card>
                <CardHeader>
                  <CardTitle>B2B Elements Assessment</CardTitle>
                  <CardDescription>
                    Coming Soon - Individual assessment component
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <Button disabled className="w-full">
                    <Building2 className="mr-2 h-4 w-4" />
                    B2B Elements Analysis
                  </Button>
                </CardContent>
              </Card>
            </TabsContent>

            <TabsContent value="comprehensive" className="mt-6">
              <Card>
                <CardHeader>
                  <CardTitle>Comprehensive Report</CardTitle>
                  <CardDescription>
                    Combined analysis from all frameworks
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <Button
                    onClick={runComprehensiveAnalysis}
                    disabled={isLoading}
                    className="w-full"
                  >
                    {isLoading ? (
                      <>
                        <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                        Generating Comprehensive Report...
                      </>
                    ) : (
                      <>
                        <FileText className="mr-2 h-4 w-4" />
                        Generate Comprehensive Report
                      </>
                    )}
                  </Button>

                  {analysisData?.comprehensive && (
                    <div className="space-y-4">
                      <div className="p-4 bg-green-50 border border-green-200 rounded">
                        <h3 className="font-semibold text-green-800 mb-2">Report Generated Successfully</h3>
                        <p className="text-sm text-green-700">
                          Overall Score: {analysisData.comprehensive.overallScore}/10
                        </p>
                      </div>

                      <Button onClick={downloadReport} className="w-full">
                        <Download className="mr-2 h-4 w-4" />
                        Download Report
                      </Button>
                    </div>
                  )}
                </CardContent>
              </Card>
            </TabsContent>
          </Tabs>
        )}
      </div>
    </div>
  );
}

/**
 * CliftonStrengths Analysis Page
 * Individual page for CliftonStrengths analysis with report viewing
 */

'use client';

import { Alert, AlertDescription } from '@/components/ui/alert';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
    Brain,
    Clock,
    Download,
    ExternalLink,
    Users,
    XCircle
} from 'lucide-react';
import Link from 'next/link';
import { useState } from 'react';

interface ThemeScore {
  theme_name: string;
  domain: string;
  score: number;
  evidence: {
    patterns: string[];
    citations: string[];
    confidence: number;
  };
  manifestation_description: string;
}

interface CliftonStrengthsAnalysis {
  overall_score: number;
  strategic_thinking_score: number;
  relationship_building_score: number;
  influencing_score: number;
  executing_score: number;
  themes: ThemeScore[];
}

export default function CliftonStrengthsPage() {
  const [url, setUrl] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [analysis, setAnalysis] = useState<CliftonStrengthsAnalysis | null>(null);
  const [analysisId, setAnalysisId] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);

  const runAnalysis = async () => {
    if (!url) return;

    setIsAnalyzing(true);
    setError(null);

    try {
      // Run Phase 1: Data Collection
      const phase1Response = await fetch('/api/analyze/phase-new', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url, phase: 1 })
      });

      if (!phase1Response.ok) {
        throw new Error('Phase 1 failed');
      }

      const phase1Data = await phase1Response.json();
      const newAnalysisId = phase1Data.analysisId;
      setAnalysisId(newAnalysisId);

      // Run Phase 2: Framework Analysis (includes CliftonStrengths)
      const phase2Response = await fetch('/api/analyze/phase-new', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url, phase: 2, analysisId: newAnalysisId })
      });

      if (!phase2Response.ok) {
        throw new Error('Phase 2 failed');
      }

      // Fetch CliftonStrengths analysis
      const cliftonResponse = await fetch(`/api/analysis/clifton-strengths/${newAnalysisId}`);
      if (cliftonResponse.ok) {
        const cliftonData = await cliftonResponse.json();
        setAnalysis(cliftonData);
      }

    } catch (err) {
      setError(err instanceof Error ? err.message : 'Analysis failed');
    } finally {
      setIsAnalyzing(false);
    }
  };

  const getScoreColor = (score: number) => {
    if (score >= 80) return 'text-green-600';
    if (score >= 60) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getScoreBadge = (score: number) => {
    if (score >= 80) return <Badge className="bg-green-100 text-green-800">Strong</Badge>;
    if (score >= 60) return <Badge className="bg-yellow-100 text-yellow-800">Moderate</Badge>;
    return <Badge className="bg-red-100 text-red-800">Weak</Badge>;
  };

  const getDomainColor = (domain: string) => {
    switch (domain.toLowerCase()) {
      case 'strategic thinking': return 'bg-blue-100 text-blue-800';
      case 'relationship building': return 'bg-purple-100 text-purple-800';
      case 'influencing': return 'bg-orange-100 text-orange-800';
      case 'executing': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const renderThemesByDomain = (themes: ThemeScore[]) => {
    const domains = {
      'Strategic Thinking': themes.filter(t => t.domain === 'Strategic Thinking'),
      'Relationship Building': themes.filter(t => t.domain === 'Relationship Building'),
      'Influencing': themes.filter(t => t.domain === 'Influencing'),
      'Executing': themes.filter(t => t.domain === 'Executing')
    };

    return (
      <div className="space-y-6">
        {Object.entries(domains).map(([domain, domainThemes]) => (
          <Card key={domain}>
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                <span>{domain}</span>
                <Badge className={getDomainColor(domain)}>
                  {domainThemes.length} themes
                </Badge>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid gap-4">
                {domainThemes
                  .sort((a, b) => b.score - a.score)
                  .map((theme, index) => (
                  <div key={index} className="border rounded-lg p-4">
                    <div className="flex items-center justify-between mb-2">
                      <h4 className="font-medium capitalize">
                        {theme.theme_name.replace(/_/g, ' ')}
                      </h4>
                      <div className="flex items-center gap-2">
                        <span className={`font-bold ${getScoreColor(theme.score)}`}>
                          {theme.score}
                        </span>
                        {getScoreBadge(theme.score)}
                      </div>
                    </div>

                    <div className="text-sm text-gray-600 mb-2">
                      Confidence: {(theme.evidence.confidence * 100).toFixed(0)}%
                    </div>

                    <div className="text-sm text-gray-700 mb-3">
                      {theme.manifestation_description}
                    </div>

                    {theme.evidence.patterns.length > 0 && (
                      <div className="mb-2">
                        <div className="text-sm font-medium mb-1">Detected Patterns:</div>
                        <div className="flex flex-wrap gap-1">
                          {theme.evidence.patterns.map((pattern, i) => (
                            <Badge key={i} variant="outline" className="text-xs">
                              {pattern}
                            </Badge>
                          ))}
                        </div>
                      </div>
                    )}

                    {theme.evidence.citations.length > 0 && (
                      <div>
                        <div className="text-sm font-medium mb-1">Evidence:</div>
                        <ul className="text-xs text-gray-600 list-disc list-inside">
                          {theme.evidence.citations.map((citation, i) => (
                            <li key={i}>{citation}</li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  };

  return (
    <div className="container mx-auto py-8 px-4">
      <div className="mb-8">
        <h1 className="text-3xl font-bold mb-2">CliftonStrengths Analysis</h1>
        <p className="text-gray-600">
          Analyze your website&apos;s organizational strengths using Gallup&apos;s 34-theme framework
        </p>
      </div>

      {!analysis ? (
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Users className="h-5 w-5" />
              Start CliftonStrengths Analysis
            </CardTitle>
            <CardDescription>
              Enter your website URL to analyze your organizational strengths and themes
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="url">Website URL</Label>
              <Input
                id="url"
                type="url"
                placeholder="https://example.com"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
              />
            </div>

            {error && (
              <Alert variant="destructive">
                <XCircle className="h-4 w-4" />
                <AlertDescription>{error}</AlertDescription>
              </Alert>
            )}

            <Button
              onClick={runAnalysis}
              disabled={!url || isAnalyzing}
              className="w-full"
            >
              {isAnalyzing ? (
                <>
                  <Clock className="mr-2 h-4 w-4 animate-spin" />
                  Analyzing...
                </>
              ) : (
                <>
                  <Brain className="mr-2 h-4 w-4" />
                  Analyze CliftonStrengths
                </>
              )}
            </Button>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-6">
          {/* Overall Score */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                <span>Overall CliftonStrengths Score</span>
                <div className="flex items-center gap-2">
                  <span className={`text-3xl font-bold ${getScoreColor(analysis.overall_score)}`}>
                    {analysis.overall_score}
                  </span>
                  {getScoreBadge(analysis.overall_score)}
                </div>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                <div className="text-center">
                  <div className="text-2xl font-bold text-blue-600">{analysis.strategic_thinking_score}</div>
                  <div className="text-sm text-gray-600">Strategic Thinking</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-purple-600">{analysis.relationship_building_score}</div>
                  <div className="text-sm text-gray-600">Relationship Building</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-orange-600">{analysis.influencing_score}</div>
                  <div className="text-sm text-gray-600">Influencing</div>
                </div>
                <div className="text-center">
                  <div className="text-2xl font-bold text-green-600">{analysis.executing_score}</div>
                  <div className="text-sm text-gray-600">Executing</div>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Top 10 Strengths */}
          <Card>
            <CardHeader>
              <CardTitle>Top 10 Strengths</CardTitle>
              <CardDescription>
                Your strongest themes based on website content analysis
              </CardDescription>
            </CardHeader>
            <CardContent>
              <div className="space-y-3">
                {analysis.themes
                  .sort((a, b) => b.score - a.score)
                  .slice(0, 10)
                  .map((theme, index) => (
                  <div key={index} className="flex items-center justify-between p-3 border rounded-lg">
                    <div className="flex items-center gap-3">
                      <div className="w-8 h-8 bg-blue-100 text-blue-800 rounded-full flex items-center justify-center text-sm font-bold">
                        {index + 1}
                      </div>
                      <div>
                        <div className="font-medium capitalize">
                          {theme.theme_name.replace(/_/g, ' ')}
                        </div>
                        <div className="text-sm text-gray-600">{theme.domain}</div>
                      </div>
                    </div>
                    <div className="flex items-center gap-2">
                      <span className={`font-bold ${getScoreColor(theme.score)}`}>
                        {theme.score}
                      </span>
                      {getScoreBadge(theme.score)}
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>

          {/* Detailed Analysis by Domain */}
          <Card>
            <CardHeader>
              <CardTitle>Detailed Analysis by Domain</CardTitle>
              <CardDescription>
                Complete breakdown of all 34 themes across the four domains
              </CardDescription>
            </CardHeader>
            <CardContent>
              {renderThemesByDomain(analysis.themes)}
            </CardContent>
          </Card>

          {/* Action Buttons */}
          <div className="flex gap-4">
            <Button onClick={() => setAnalysis(null)} variant="outline">
              Analyze Another Website
            </Button>
            <Button variant="outline">
              <Download className="mr-2 h-4 w-4" />
              Download Report
            </Button>
            <Link href="/dashboard/analysis">
              <Button variant="outline">
                <ExternalLink className="mr-2 h-4 w-4" />
                View All Analyses
              </Button>
            </Link>
          </div>
        </div>
      )}
    </div>
  );
}

/**
 * Elements of Value Analysis Page
 * Individual page for Elements of Value analysis (B2C & B2B)
 */

'use client';

import { Alert, AlertDescription } from '@/components/ui/alert';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import {
    BarChart3,
    Clock,
    Download,
    ExternalLink,
    Target,
    XCircle
} from 'lucide-react';
import Link from 'next/link';
import { useState } from 'react';

interface ElementScore {
  element_name: string;
  element_category: string;
  pyramid_level: number;
  score: number;
  evidence: {
    patterns: string[];
    citations: string[];
    confidence: number;
  };
}

interface ElementsAnalysis {
  overall_score: number;
  functional_score: number;
  emotional_score: number;
  life_changing_score: number;
  social_impact_score: number;
  elements: ElementScore[];
}

export default function ElementsOfValuePage() {
  const [url, setUrl] = useState('');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [b2cAnalysis, setB2cAnalysis] = useState<ElementsAnalysis | null>(null);
  const [b2bAnalysis, setB2bAnalysis] = useState<ElementsAnalysis | null>(null);
  const [analysisId, setAnalysisId] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [activeTab, setActiveTab] = useState<'b2c' | 'b2b'>('b2c');

  const runAnalysis = async () => {
    if (!url) return;

    setIsAnalyzing(true);
    setError(null);

    try {
      // Run Phase 1: Data Collection
      const phase1Response = await fetch('/api/analyze/phase-new', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url, phase: 1 })
      });

      if (!phase1Response.ok) {
        throw new Error('Phase 1 failed');
      }

      const phase1Data = await phase1Response.json();
      const newAnalysisId = phase1Data.analysisId;
      setAnalysisId(newAnalysisId);

      // Run Phase 2: Framework Analysis (includes Elements of Value)
      const phase2Response = await fetch('/api/analyze/phase-new', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ url, phase: 2, analysisId: newAnalysisId })
      });

      if (!phase2Response.ok) {
        throw new Error('Phase 2 failed');
      }

      // Fetch B2C Elements of Value analysis
      const b2cResponse = await fetch(`/api/analysis/elements-value-b2c/${newAnalysisId}`);
      if (b2cResponse.ok) {
        const b2cData = await b2cResponse.json();
        setB2cAnalysis(b2cData);
      }

      // Fetch B2B Elements of Value analysis
      const b2bResponse = await fetch(`/api/analysis/elements-value-b2b/${newAnalysisId}`);
      if (b2bResponse.ok) {
        const b2bData = await b2bResponse.json();
        setB2bAnalysis(b2bData);
      }

    } catch (err) {
      setError(err instanceof Error ? err.message : 'Analysis failed');
    } finally {
      setIsAnalyzing(false);
    }
  };

  const getScoreColor = (score: number) => {
    if (score >= 80) return 'text-green-600';
    if (score >= 60) return 'text-yellow-600';
    return 'text-red-600';
  };

  const getScoreBadge = (score: number) => {
    if (score >= 80) return <Badge className="bg-green-100 text-green-800">Strong</Badge>;
    if (score >= 60) return <Badge className="bg-yellow-100 text-yellow-800">Moderate</Badge>;
    return <Badge className="bg-red-100 text-red-800">Weak</Badge>;
  };

  const getCategoryColor = (category: string) => {
    switch (category) {
      case 'functional': return 'bg-blue-100 text-blue-800';
      case 'emotional': return 'bg-purple-100 text-purple-800';
      case 'life_changing': return 'bg-green-100 text-green-800';
      case 'social_impact': return 'bg-orange-100 text-orange-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const renderElementsTable = (analysis: ElementsAnalysis) => {
    const categories = {
      functional: analysis.elements.filter(e => e.element_category === 'functional'),
      emotional: analysis.elements.filter(e => e.element_category === 'emotional'),
      life_changing: analysis.elements.filter(e => e.element_category === 'life_changing'),
      social_impact: analysis.elements.filter(e => e.element_category === 'social_impact')
    };

    return (
      <div className="space-y-6">
        {Object.entries(categories).map(([category, elements]) => (
          <Card key={category}>
            <CardHeader>
              <CardTitle className="flex items-center justify-between">
                <span className="capitalize">{category.replace('_', ' ')} Elements</span>
                <Badge className={getCategoryColor(category)}>
                  {elements.length} elements
                </Badge>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid gap-4">
                {elements.map((element, index) => (
                  <div key={index} className="border rounded-lg p-4">
                    <div className="flex items-center justify-between mb-2">
                      <h4 className="font-medium capitalize">
                        {element.element_name.replace(/_/g, ' ')}
                      </h4>
                      <div className="flex items-center gap-2">
                        <span className={`font-bold ${getScoreColor(element.score)}`}>
                          {element.score}
                        </span>
                        {getScoreBadge(element.score)}
                      </div>
                    </div>

                    <div className="text-sm text-gray-600 mb-2">
                      Pyramid Level {element.pyramid_level} â€¢ Confidence: {(element.evidence.confidence * 100).toFixed(0)}%
                    </div>

                    {element.evidence.patterns.length > 0 && (
                      <div className="mb-2">
                        <div className="text-sm font-medium mb-1">Detected Patterns:</div>
                        <div className="flex flex-wrap gap-1">
                          {element.evidence.patterns.map((pattern, i) => (
                            <Badge key={i} variant="outline" className="text-xs">
                              {pattern}
                            </Badge>
                          ))}
                        </div>
                      </div>
                    )}

                    {element.evidence.citations.length > 0 && (
                      <div>
                        <div className="text-sm font-medium mb-1">Evidence:</div>
                        <ul className="text-xs text-gray-600 list-disc list-inside">
                          {element.evidence.citations.map((citation, i) => (
                            <li key={i}>{citation}</li>
                          ))}
                        </ul>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    );
  };

  return (
    <div className="container mx-auto py-8 px-4">
      <div className="mb-8">
        <h1 className="text-3xl font-bold mb-2">Elements of Value Analysis</h1>
        <p className="text-gray-600">
          Analyze your website&apos;s value proposition using Bain & Company&apos;s 30 B2C and 40 B2B value elements
        </p>
      </div>

      {!b2cAnalysis && !b2bAnalysis ? (
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <BarChart3 className="h-5 w-5" />
              Start Elements of Value Analysis
            </CardTitle>
            <CardDescription>
              Enter your website URL to analyze your value proposition across all elements
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="url">Website URL</Label>
              <Input
                id="url"
                type="url"
                placeholder="https://example.com"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
              />
            </div>

            {error && (
              <Alert variant="destructive">
                <XCircle className="h-4 w-4" />
                <AlertDescription>{error}</AlertDescription>
              </Alert>
            )}

            <Button
              onClick={runAnalysis}
              disabled={!url || isAnalyzing}
              className="w-full"
            >
              {isAnalyzing ? (
                <>
                  <Clock className="mr-2 h-4 w-4 animate-spin" />
                  Analyzing...
                </>
              ) : (
                <>
                  <Target className="mr-2 h-4 w-4" />
                  Analyze Elements of Value
                </>
              )}
            </Button>
          </CardContent>
        </Card>
      ) : (
        <div className="space-y-6">
          {/* Overall Scores */}
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {b2cAnalysis && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center justify-between">
                    <span>B2C Elements Score</span>
                    <div className="flex items-center gap-2">
                      <span className={`text-3xl font-bold ${getScoreColor(b2cAnalysis.overall_score)}`}>
                        {b2cAnalysis.overall_score}
                      </span>
                      {getScoreBadge(b2cAnalysis.overall_score)}
                    </div>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-2 gap-4">
                    <div className="text-center">
                      <div className="text-xl font-bold text-blue-600">{b2cAnalysis.functional_score}</div>
                      <div className="text-sm text-gray-600">Functional</div>
                    </div>
                    <div className="text-center">
                      <div className="text-xl font-bold text-purple-600">{b2cAnalysis.emotional_score}</div>
                      <div className="text-sm text-gray-600">Emotional</div>
                    </div>
                    <div className="text-center">
                      <div className="text-xl font-bold text-green-600">{b2cAnalysis.life_changing_score}</div>
                      <div className="text-sm text-gray-600">Life Changing</div>
                    </div>
                    <div className="text-center">
                      <div className="text-xl font-bold text-orange-600">{b2cAnalysis.social_impact_score}</div>
                      <div className="text-sm text-gray-600">Social Impact</div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            )}

            {b2bAnalysis && (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center justify-between">
                    <span>B2B Elements Score</span>
                    <div className="flex items-center gap-2">
                      <span className={`text-3xl font-bold ${getScoreColor(b2bAnalysis.overall_score)}`}>
                        {b2bAnalysis.overall_score}
                      </span>
                      {getScoreBadge(b2bAnalysis.overall_score)}
                    </div>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-2 gap-4">
                    <div className="text-center">
                      <div className="text-xl font-bold text-blue-600">{b2bAnalysis.functional_score}</div>
                      <div className="text-sm text-gray-600">Functional</div>
                    </div>
                    <div className="text-center">
                      <div className="text-xl font-bold text-purple-600">{b2bAnalysis.emotional_score}</div>
                      <div className="text-sm text-gray-600">Emotional</div>
                    </div>
                    <div className="text-center">
                      <div className="text-xl font-bold text-green-600">{b2bAnalysis.life_changing_score}</div>
                      <div className="text-sm text-gray-600">Life Changing</div>
                    </div>
                    <div className="text-center">
                      <div className="text-xl font-bold text-orange-600">{b2bAnalysis.social_impact_score}</div>
                      <div className="text-sm text-gray-600">Social Impact</div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            )}
          </div>

          {/* Detailed Analysis Tabs */}
          <Tabs value={activeTab} onValueChange={(value) => setActiveTab(value as 'b2c' | 'b2b')}>
            <TabsList className="grid w-full grid-cols-2">
              <TabsTrigger value="b2c">B2C Elements (30)</TabsTrigger>
              <TabsTrigger value="b2b">B2B Elements (40)</TabsTrigger>
            </TabsList>

            <TabsContent value="b2c">
              {b2cAnalysis ? renderElementsTable(b2cAnalysis) : (
                <Card>
                  <CardContent className="p-8 text-center">
                    <p className="text-gray-500">B2C analysis not available</p>
                  </CardContent>
                </Card>
              )}
            </TabsContent>

            <TabsContent value="b2b">
              {b2bAnalysis ? renderElementsTable(b2bAnalysis) : (
                <Card>
                  <CardContent className="p-8 text-center">
                    <p className="text-gray-500">B2B analysis not available</p>
                  </CardContent>
                </Card>
              )}
            </TabsContent>
          </Tabs>

          {/* Action Buttons */}
          <div className="flex gap-4">
            <Button onClick={() => {
              setB2cAnalysis(null);
              setB2bAnalysis(null);
            }} variant="outline">
              Analyze Another Website
            </Button>
            <Button variant="outline">
              <Download className="mr-2 h-4 w-4" />
              Download Report
            </Button>
            <Link href="/dashboard/analysis">
              <Button variant="outline">
                <ExternalLink className="mr-2 h-4 w-4" />
                View All Analyses
              </Button>
            </Link>
          </div>
        </div>
      )}
    </div>
  );
}

