      )}

      {!loading && !error && (
        <Tabs value={activeTab} onValueChange={setActiveTab} className="w-full">
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="phase1">
              Phase 1: Data Collection ({phase1Reports.length})
            </TabsTrigger>
            <TabsTrigger value="phase2">
