'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { CheckCircle, Circle, Clock, AlertCircle } from 'lucide-react';

interface AnalysisProgressWidgetProps {
  className?: string;
}

interface SimpleAnalysis {
  id: string;
  url: string;
  status: 'running' | 'completed' | 'failed';
  progress: number;
  steps: {
    baseAnalysis: boolean;
    pageAudit: boolean;
    lighthouse: boolean;
    geminiInsights: boolean;
  };
  startedAt: string;
  completedAt?: string;
}

export default function AnalysisProgressWidget({ className }: AnalysisProgressWidgetProps) {
  const [analyses, setAnalyses] = useState<SimpleAnalysis[]>([]);

  useEffect(() => {
    // Load from localStorage
    const loadAnalyses = () => {
      try {
        const saved = localStorage.getItem('simple-analyses');
        if (saved) {
          setAnalyses(JSON.parse(saved));
        }
      } catch (error) {
        console.warn('Failed to load analyses:', error);
      }
    };

    loadAnalyses();

    // Listen for storage changes
    const handleStorageChange = () => {
      loadAnalyses();
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'running':
        return <Clock className="h-4 w-4 text-blue-500 animate-spin" />;
      case 'failed':
        return <AlertCircle className="h-4 w-4 text-red-500" />;
      default:
        return <Circle className="h-4 w-4 text-gray-400" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'text-green-600';
      case 'running':
        return 'text-blue-600';
      case 'failed':
        return 'text-red-600';
      default:
        return 'text-gray-500';
    }
  };

  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString('en-US', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  const getStepIcon = (completed: boolean) => {
    return completed ? (
      <CheckCircle className="h-3 w-3 text-green-500" />
    ) : (
      <Circle className="h-3 w-3 text-gray-300" />
    );
  };

  return (
    <Card className={className}>
      <CardHeader>
        <CardTitle className="text-lg flex items-center gap-2">
          <CheckCircle className="h-5 w-5 text-blue-600" />
          Analysis Progress
        </CardTitle>
        <CardDescription>
          Track your website analysis progress
        </CardDescription>
      </CardHeader>
      <CardContent>
        {analyses.length === 0 ? (
          <div className="text-center py-4 text-gray-500">
            <Circle className="h-8 w-8 mx-auto mb-2 text-gray-300" />
            <p className="text-sm">No analyses yet</p>
          </div>
        ) : (
          <div className="space-y-3">
            {analyses.slice(0, 3).map((analysis) => (
              <div key={analysis.id} className="border rounded-lg p-3">
                {/* Header */}
                <div className="flex items-center justify-between mb-2">
                  <div className="flex items-center gap-2">
                    {getStatusIcon(analysis.status)}
                    <span className="text-sm font-medium truncate max-w-32">
                      {new URL(analysis.url).hostname}
                    </span>
                  </div>
                  <Badge variant="outline" className="text-xs">
                    {analysis.progress}%
                  </Badge>
                </div>

                {/* Progress Bar */}
                <div className="w-full bg-gray-200 rounded-full h-1.5 mb-2">
                  <div 
                    className="bg-gradient-to-r from-blue-500 to-green-500 h-1.5 rounded-full transition-all duration-300"
                    style={{ width: `${analysis.progress}%` }}
                  />
                </div>

                {/* Steps */}
                <div className="flex items-center justify-between text-xs text-gray-600">
                  <div className="flex items-center gap-1">
                    {getStepIcon(analysis.steps.baseAnalysis)}
                    <span>AI</span>
                  </div>
                  <div className="flex items-center gap-1">
                    {getStepIcon(analysis.steps.pageAudit)}
                    <span>SEO</span>
                  </div>
                  <div className="flex items-center gap-1">
                    {getStepIcon(analysis.steps.lighthouse)}
                    <span>Speed</span>
                  </div>
                  <div className="flex items-center gap-1">
                    {getStepIcon(analysis.steps.geminiInsights)}
                    <span>Insights</span>
                  </div>
                </div>

                {/* Time */}
                <div className="text-xs text-gray-500 mt-1">
                  {analysis.status === 'running' ? 'Started' : 'Completed'} at {formatTime(analysis.completedAt || analysis.startedAt)}
                </div>
              </div>
            ))}

            {analyses.length > 3 && (
              <div className="text-center pt-2">
                <span className="text-xs text-gray-500">
                  +{analyses.length - 3} more analyses
                </span>
              </div>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

/**
 * Automated Google Tools Page
 * Combines direct tool links with automated Puppeteer data extraction
 */

'use client';

import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { GoogleToolsDirectService } from '@/lib/services/google-tools-direct.service';
import {
    BarChart3,
    Bot,
    CheckCircle,
    Copy,
    Download,
    ExternalLink,
    Loader2,
    RefreshCw,
    Search,
    TrendingUp,
    X,
    Zap
} from 'lucide-react';
import { useState } from 'react';

interface GoogleToolLink {
  name: string;
  url: string;
  description: string;
  icon: string;
}

interface ScrapedData {
  trends?: any;
  pageSpeed?: any;
  searchConsole?: any;
  analytics?: any;
}

export function AutomatedGoogleToolsPage() {
  const [url, setUrl] = useState('');
  const [keywords, setKeywords] = useState('');
  const [toolLinks, setToolLinks] = useState<GoogleToolLink[]>([]);
  const [scrapedData, setScrapedData] = useState<ScrapedData | null>(null);
  const [isScraping, setIsScraping] = useState(false);
  const [selectedTool, setSelectedTool] = useState<string | null>(null);
  const [manualData, setManualData] = useState('');
  const [analysisResult, setAnalysisResult] = useState<string | null>(null);
  const [copiedTool, setCopiedTool] = useState<string | null>(null);

  // Clear previous searches when URL changes
  const handleUrlChange = (newUrl: string) => {
    setUrl(newUrl);
    if (newUrl !== url) {
      setToolLinks([]);
      setScrapedData(null);
      setSelectedTool(null);
      setManualData('');
      setAnalysisResult(null);
      setCopiedTool(null);
    }
  };

  const handleGenerateLinks = () => {
    if (!url.trim()) return;

    const keywordArray = keywords.split(',').map(k => k.trim()).filter(k => k.length > 0);
    const links = GoogleToolsDirectService.getToolLinks(url, keywordArray);
    setToolLinks(links);
  };

  const handleScrapeData = async () => {
    if (!url.trim()) return;

    setIsScraping(true);
    try {
      const keywordArray = keywords.split(',').map(k => k.trim()).filter(k => k.length > 0);

      const response = await fetch('/api/scrape-google-tools', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          url,
          keywords: keywordArray
        }),
      });

      const result = await response.json();

      if (result.success) {
        setScrapedData(result.data);
        console.log('Scraped data:', result.data);
      } else {
        console.error('Scraping failed:', result.error);
        // Still show the tool links even if scraping fails
        handleGenerateLinks();
      }
    } catch (error) {
      console.error('Scraping error:', error);
      // Fallback to manual links
      handleGenerateLinks();
    } finally {
      setIsScraping(false);
    }
  };

  const handleCopyLink = (toolName: string, url: string) => {
    navigator.clipboard.writeText(url);
    setCopiedTool(toolName);
    setTimeout(() => setCopiedTool(null), 2000);
  };

  const handleClearAll = () => {
    setUrl('');
    setKeywords('');
    setToolLinks([]);
    setScrapedData(null);
    setSelectedTool(null);
    setManualData('');
    setAnalysisResult(null);
    setCopiedTool(null);
  };

  const handleAnalyzeData = async () => {
    if (!selectedTool || !manualData.trim()) return;

    try {
      const prompts = GoogleToolsDirectService.getPTCFPrompts();
      const prompt = prompts[selectedTool];

      if (!prompt) return;

      const fullPrompt = `${prompt.persona}

${prompt.task}

${prompt.context.replace('[PASTE', manualData)}

${prompt.format}`;

      // For now, just display the prompt - in production, this would send to Gemini
      setAnalysisResult(fullPrompt);
    } catch (error) {
      console.error('Analysis failed:', error);
    }
  };

  const getToolIcon = (toolName: string) => {
    switch (toolName) {
      case 'Google Trends': return <TrendingUp className="h-6 w-6" />;
      case 'PageSpeed Insights': return <Zap className="h-6 w-6" />;
      case 'Google Search Console': return <Search className="h-6 w-6" />;
      case 'Google Analytics': return <BarChart3 className="h-6 w-6" />;
      case 'Lighthouse Audit': return <Zap className="h-6 w-6" />;
      case 'GTmetrix Analysis': return <BarChart3 className="h-6 w-6" />;
      default: return <ExternalLink className="h-6 w-6" />;
    }
  };

  const downloadScrapedData = () => {
    if (!scrapedData) return;

    const dataStr = JSON.stringify(scrapedData, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });
    const url = URL.createObjectURL(dataBlob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `google-tools-data-${new Date().toISOString().split('T')[0]}.json`;
    link.click();
    URL.revokeObjectURL(url);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 dark:from-slate-900 dark:to-slate-800">
      <div className="container mx-auto py-8 px-4">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-4">
            Automated Google Tools Analysis
          </h1>
          <p className="text-xl text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
            Get direct access to Google Tools AND automatically scrape data for AI analysis
          </p>
        </div>

        {/* Input Form */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center">
              <Bot className="mr-2 h-6 w-6 text-blue-600" />
              Automated Data Collection
            </CardTitle>
            <CardDescription>
              Enter your website URL and keywords to get both direct tool links and automated data extraction
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div>
                <Label htmlFor="url">Website URL</Label>
                <Input
                  id="url"
                  type="url"
                  placeholder="https://example.com"
                  value={url}
                  onChange={(e) => handleUrlChange(e.target.value)}
                  className="mt-1"
                />
              </div>
              <div>
                <Label htmlFor="keywords">Keywords (comma-separated)</Label>
                <Input
                  id="keywords"
                  type="text"
                  placeholder="custom homes, home builders, construction"
                  value={keywords}
                  onChange={(e) => setKeywords(e.target.value)}
                  className="mt-1"
                />
              </div>
              <div className="flex gap-2">
                <Button
                  onClick={handleScrapeData}
                  disabled={!url.trim() || isScraping}
                  className="flex-1"
                >
                  {isScraping ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      Scraping Data...
                    </>
                  ) : (
                    <>
                      <Bot className="mr-2 h-4 w-4" />
                      Auto-Scrape All Data
                    </>
                  )}
                </Button>
                <Button
                  onClick={handleGenerateLinks}
                  disabled={!url.trim()}
                  variant="outline"
                  className="flex-1"
                >
                  <Search className="mr-2 h-4 w-4" />
                  Manual Links Only
                </Button>
                <Button
                  onClick={handleClearAll}
                  variant="outline"
                  disabled={!url.trim() && !keywords.trim() && toolLinks.length === 0}
                >
                  <X className="mr-2 h-4 w-4" />
                  Clear All
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Scraped Data Results */}
        {scrapedData && (
          <Card className="mb-8 border-green-200 bg-green-50 dark:bg-green-900/10">
            <CardHeader>
              <div className="flex items-center justify-between">
                <div className="flex items-center">
                  <CheckCircle className="mr-2 h-6 w-6 text-green-600" />
                  <div>
                    <CardTitle>Automatically Scraped Data</CardTitle>
                    <CardDescription>
                      Data successfully extracted from Google Tools using Puppeteer
                    </CardDescription>
                  </div>
                </div>
                <Button
                  onClick={downloadScrapedData}
                  variant="outline"
                  size="sm"
                >
                  <Download className="mr-2 h-4 w-4" />
                  Download Data
                </Button>
              </div>
            </CardHeader>
            <CardContent>
              <div className="grid gap-4 md:grid-cols-2">
                {scrapedData.trends && (
                  <Card className="border-l-4 border-l-blue-500">
                    <CardContent className="pt-4">
                      <div className="flex items-center space-x-2 mb-2">
                        <TrendingUp className="h-6 w-6 text-blue-600" />
                        <h3 className="font-semibold text-lg">Google Trends</h3>
                        <Badge variant="secondary">Auto-Scraped</Badge>
                      </div>
                      <p className="text-sm text-gray-600 dark:text-gray-400">
                        Related queries: {scrapedData.trends.relatedQueries?.length || 0}
                      </p>
                      <p className="text-sm text-gray-600 dark:text-gray-400">
                        Related topics: {scrapedData.trends.relatedTopics?.length || 0}
                      </p>
                    </CardContent>
                  </Card>
                )}

                {scrapedData.pageSpeed && (
                  <Card className="border-l-4 border-l-green-500">
                    <CardContent className="pt-4">
                      <div className="flex items-center space-x-2 mb-2">
                        <Zap className="h-6 w-6 text-green-600" />
                        <h3 className="font-semibold text-lg">PageSpeed Insights</h3>
                        <Badge variant="secondary">Auto-Scraped</Badge>
                      </div>
                      <p className="text-sm text-gray-600 dark:text-gray-400">
                        Performance: {scrapedData.pageSpeed.performanceScore || 0}/100
                      </p>
                      <p className="text-sm text-gray-600 dark:text-gray-400">
                        Opportunities: {scrapedData.pageSpeed.opportunities?.length || 0}
                      </p>
                    </CardContent>
                  </Card>
                )}

                {scrapedData.searchConsole && (
                  <Card className="border-l-4 border-l-purple-500">
                    <CardContent className="pt-4">
                      <div className="flex items-center space-x-2 mb-2">
                        <Search className="h-6 w-6 text-purple-600" />
                        <h3 className="font-semibold text-lg">Search Console</h3>
                        <Badge variant="outline">Auth Required</Badge>
                      </div>
                      <p className="text-sm text-gray-600 dark:text-gray-400">
                        Requires Google Search Console authentication
                      </p>
                    </CardContent>
                  </Card>
                )}

                {scrapedData.analytics && (
                  <Card className="border-l-4 border-l-orange-500">
                    <CardContent className="pt-4">
                      <div className="flex items-center space-x-2 mb-2">
                        <BarChart3 className="h-6 w-6 text-orange-600" />
                        <h3 className="font-semibold text-lg">Google Analytics</h3>
                        <Badge variant="outline">Auth Required</Badge>
                      </div>
                      <p className="text-sm text-gray-600 dark:text-gray-400">
                        Requires Google Analytics authentication
                      </p>
                    </CardContent>
                  </Card>
                )}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Google Tools Links */}
        {toolLinks.length > 0 && (
          <Card className="mb-8">
            <CardHeader>
              <div className="flex items-center justify-between">
                <div className="flex items-center">
                  <CheckCircle className="mr-2 h-6 w-6 text-green-600" />
                  <div>
                    <CardTitle>Google Tools Access</CardTitle>
                    <CardDescription>
                      Click on any tool to open it in a new tab, or copy the link to share
                    </CardDescription>
                  </div>
                </div>
                <Button
                  onClick={handleGenerateLinks}
                  variant="outline"
                  size="sm"
                >
                  <RefreshCw className="mr-2 h-4 w-4" />
                  Refresh Links
                </Button>
              </div>
            </CardHeader>
            <CardContent>
              <div className="grid gap-4 md:grid-cols-2">
                {toolLinks.map((tool, index) => (
                  <Card key={index} className="border-l-4 border-l-blue-500">
                    <CardContent className="pt-4">
                      <div className="flex items-center justify-between mb-2">
                        <div className="flex items-center space-x-2">
                          {getToolIcon(tool.name)}
                          <h3 className="font-semibold text-lg">{tool.name}</h3>
                        </div>
                        <div className="flex space-x-2">
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => handleCopyLink(tool.name, tool.url)}
                          >
                            {copiedTool === tool.name ? (
                              <CheckCircle className="h-4 w-4 text-green-600" />
                            ) : (
                              <Copy className="h-4 w-4" />
                            )}
                          </Button>
                          <Button
                            size="sm"
                            onClick={() => window.open(tool.url, '_blank')}
                          >
                            <ExternalLink className="h-4 w-4" />
                          </Button>
                        </div>
                      </div>
                      <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">
                        {tool.description}
                      </p>
                      <div className="text-xs text-gray-500 font-mono break-all">
                        {tool.url}
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Manual Data Analysis */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center">
              <BarChart3 className="mr-2 h-6 w-6 text-purple-600" />
              Analyze Google Tools Data
            </CardTitle>
            <CardDescription>
              Paste data from any Google Tool to get AI-powered analysis
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div>
                <Label htmlFor="tool-select">Select Tool</Label>
                <select
                  id="tool-select"
                  value={selectedTool || ''}
                  onChange={(e) => setSelectedTool(e.target.value)}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                >
                  <option value="">Select a tool...</option>
                  <option value="trends">Google Trends</option>
                  <option value="analytics">Google Analytics</option>
                  <option value="searchConsole">Google Search Console</option>
                  <option value="pageSpeed">PageSpeed Insights</option>
                </select>
              </div>

              {selectedTool && (
                <div>
                  <Label htmlFor="manual-data">Paste Data from {selectedTool}</Label>
                  <Textarea
                    id="manual-data"
                    placeholder={GoogleToolsDirectService.getDataInputPrompts()[selectedTool]}
                    value={manualData}
                    onChange={(e) => setManualData(e.target.value)}
                    className="mt-1 min-h-[200px]"
                  />
                </div>
              )}

              <Button
                onClick={handleAnalyzeData}
                disabled={!selectedTool || !manualData.trim()}
                className="w-full"
              >
                Analyze Data with AI
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Analysis Results */}
        {analysisResult && (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <CheckCircle className="mr-2 h-6 w-6 text-green-600" />
                Analysis Result
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="bg-gray-50 dark:bg-gray-800 p-4 rounded-lg">
                <pre className="whitespace-pre-wrap text-sm">
                  {analysisResult}
                </pre>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

/**
 * Claude Project Test Page
 * Demonstrates the Claude project integration system
 */

'use client';

import React from 'react';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
    BarChart3,
    Bot,
    Brain,
    CheckCircle,
    ExternalLink,
    Loader2,
    Target,
    Users,
    Zap
} from 'lucide-react';
import { useState } from 'react';
import { ContentPreviewBox } from './ContentPreviewBox';

interface ClaudeAnalysisResult {
  success: boolean;
  url: string;
  assessmentType: string;
  clientId: string;
  sessionId: string;
  claudeChatUrl?: string;
  analysis?: any;
  error?: string;
  timestamp: string;
  projectUrl: string;
}

export function ClaudeProjectTestPage() {
  const [url, setUrl] = useState('');
  const [isScraping, setIsScraping] = useState(false);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [scrapedContent, setScrapedContent] = useState<any>(null);
  const [selectedAssessment, setSelectedAssessment] = useState('golden-circle');
  const [result, setResult] = useState<ClaudeAnalysisResult | null>(null);
  const [error, setError] = useState<string | null>(null);

  const assessmentTypes = [
    { id: 'golden-circle', name: 'Golden Circle Analysis', icon: Target, color: 'blue' },
    { id: 'elements-value-b2c', name: 'B2C Elements of Value', icon: Users, color: 'green' },
    { id: 'elements-value-b2b', name: 'B2B Elements of Value', icon: BarChart3, color: 'purple' },
    { id: 'clifton-strengths', name: 'CliftonStrengths Analysis', icon: Brain, color: 'orange' },
    { id: 'content-comparison', name: 'Content Comparison', icon: Zap, color: 'red' }
  ];

  const handleScrapeContent = async () => {
    if (!url.trim()) {
      setError('Please enter a website URL');
      return;
    }

    setIsScraping(true);
    setError(null);
    setScrapedContent(null);
    setResult(null);

    try {
      const response = await fetch('/api/scrape-content', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url }),
      });

      const data = await response.json();

      if (data.success) {
        setScrapedContent(data.data);
        console.log('âœ… Content scraped successfully');
      } else {
        throw new Error(data.error || 'Failed to scrape content');
      }
    } catch (error) {
      console.error('âŒ Content scraping failed:', error);
      setError(error instanceof Error ? error.message : 'Failed to scrape content');
    } finally {
      setIsScraping(false);
    }
  };

  const handleAnalyzeWithClaude = async () => {
    if (!scrapedContent) {
      setError('Please scrape content first');
      return;
    }

    setIsAnalyzing(true);
    setError(null);

    try {
      const response = await fetch('/api/analyze/claude-project', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          url,
          scrapedData: scrapedContent,
          assessmentType: selectedAssessment
        }),
      });

      const data = await response.json();

      if (data.success) {
        setResult(data);
        console.log('âœ… Claude project analysis completed');
      } else {
        throw new Error(data.error || 'Claude analysis failed');
      }
    } catch (error) {
      console.error('âŒ Claude analysis failed:', error);
      setError(error instanceof Error ? error.message : 'Analysis failed');
    } finally {
      setIsAnalyzing(false);
    }
  };

  const getAssessmentIcon = (assessmentId: string) => {
    const assessment = assessmentTypes.find(a => a.id === assessmentId);
    return assessment ? assessment.icon : Brain;
  };

  const getAssessmentColor = (assessmentId: string) => {
    const assessment = assessmentTypes.find(a => a.id === assessmentId);
    return assessment ? assessment.color : 'gray';
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 dark:from-slate-900 dark:to-slate-800">
      <div className="container mx-auto py-8 px-4 max-w-6xl">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-4">
            Claude Project Integration Test
          </h1>
          <p className="text-xl text-gray-600 dark:text-gray-300">
            Test the Claude project integration system with fresh client sessions
          </p>
          <div className="mt-4">
            <Badge variant="outline" className="text-sm">
              Project: Zero Barriers Growth Accelerator
            </Badge>
          </div>
        </div>

        {/* Input Form */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center">
              <Bot className="mr-2 h-6 w-6 text-blue-600" />
              Create Fresh Claude Session
            </CardTitle>
            <CardDescription>
              Each client gets a unique Claude chat session within the project
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="website-url">Website URL</Label>
              <Input
                id="website-url"
                name="website-url"
                type="url"
                placeholder="https://example.com"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                disabled={isScraping || isAnalyzing}
                aria-label="Enter website URL to analyze"
                aria-describedby="url-help"
                required
              />
              <p id="url-help" className="text-xs text-muted-foreground mt-1">
                Enter the URL of the website you want to analyze
              </p>
            </div>

            <div>
              <Label htmlFor="assessment-type">Assessment Type</Label>
              <select
                id="assessment-type"
                value={selectedAssessment}
                onChange={(e) => setSelectedAssessment(e.target.value)}
                className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                disabled={isScraping || isAnalyzing}
              >
                {assessmentTypes.map((assessment) => (
                  <option key={assessment.id} value={assessment.id}>
                    {assessment.name}
                  </option>
                ))}
              </select>
            </div>

            <div className="flex gap-2">
              <Button
                onClick={handleScrapeContent}
                disabled={isScraping || !url.trim()}
                className="flex-1"
              >
                {isScraping ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Scraping Content...
                  </>
                ) : (
                  <>
                    <Target className="mr-2 h-4 w-4" />
                    Step 1: Scrape Content
                  </>
                )}
              </Button>

              <Button
                onClick={handleAnalyzeWithClaude}
                disabled={isAnalyzing || !scrapedContent}
                className="flex-1"
                variant="outline"
              >
                {isAnalyzing ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Analyzing with Claude...
                  </>
                ) : (
                  <>
                    <Bot className="mr-2 h-4 w-4" />
                    Step 2: Claude Analysis
                  </>
                )}
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Error Display */}
        {error && (
          <Card className="mb-8 border-red-200 bg-red-50 dark:bg-red-900/10">
            <CardContent className="pt-6">
              <p className="text-red-600 dark:text-red-400">{error}</p>
            </CardContent>
          </Card>
        )}

        {/* Scraped Content Preview */}
        {scrapedContent && (
          <ContentPreviewBox
            scrapedContent={scrapedContent}
            url={url}
            title="Claude Project Analysis - Scraped Content Preview"
            description="Content successfully scraped from the website. Review the data before running Claude analysis."
          />
        )}

        {/* Claude Analysis Results */}
        {result && (
          <Card className="border-green-200 bg-green-50 dark:bg-green-900/10">
            <CardHeader>
              <div className="flex items-center justify-between">
                <div className="flex items-center">
                  <CheckCircle className="mr-2 h-6 w-6 text-green-600" />
                  <div>
                    <CardTitle>Claude Project Analysis Results</CardTitle>
                    <CardDescription>
                      Analysis completed using fresh Claude session
                    </CardDescription>
                  </div>
                </div>
                <div className="flex gap-2">
                  <Button
                    onClick={() => window.open(result.claudeChatUrl, '_blank')}
                    variant="outline"
                    size="sm"
                    disabled={!result.claudeChatUrl}
                  >
                    <ExternalLink className="mr-2 h-4 w-4" />
                    View Claude Chat
                  </Button>
                  <Button
                    onClick={() => window.open(result.projectUrl, '_blank')}
                    variant="outline"
                    size="sm"
                  >
                    <ExternalLink className="mr-2 h-4 w-4" />
                    View Project
                  </Button>
                </div>
              </div>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {/* Session Info */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div className="p-4 border rounded-lg bg-white dark:bg-gray-800">
                    <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Client ID</p>
                    <p className="text-lg font-semibold">{result.clientId}</p>
                  </div>
                  <div className="p-4 border rounded-lg bg-white dark:bg-gray-800">
                    <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Session ID</p>
                    <p className="text-lg font-semibold font-mono text-xs">{result.sessionId}</p>
                  </div>
                  <div className="p-4 border rounded-lg bg-white dark:bg-gray-800">
                    <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Assessment</p>
                    <div className="flex items-center">
                      {React.createElement(getAssessmentIcon(selectedAssessment), {
                        className: `h-5 w-5 text-${getAssessmentColor(selectedAssessment)}-600 mr-2`
                      })}
                      <span className="text-lg font-semibold">
                        {assessmentTypes.find(a => a.id === selectedAssessment)?.name}
                      </span>
                    </div>
                  </div>
                </div>

                {/* Analysis Results */}
                <div className="p-4 border rounded-lg bg-white dark:bg-gray-800">
                  <h4 className="font-semibold mb-2">Analysis Results</h4>
                  <div className="bg-gray-50 dark:bg-gray-900 p-4 rounded-lg">
                    <pre className="whitespace-pre-wrap text-sm text-gray-700 dark:text-gray-300">
                      {JSON.stringify(result.analysis, null, 2)}
                    </pre>
                  </div>
                </div>

                {/* Project Links */}
                <div className="p-4 border rounded-lg bg-white dark:bg-gray-800">
                  <h4 className="font-semibold mb-2">Claude Project Links</h4>
                  <div className="space-y-2">
                    <div className="flex items-center justify-between">
                      <span className="text-sm text-gray-600 dark:text-gray-400">Project URL:</span>
                      <Button
                        onClick={() => window.open(result.projectUrl, '_blank')}
                        variant="link"
                        size="sm"
                        className="p-0 h-auto"
                      >
                        {result.projectUrl}
                      </Button>
                    </div>
                    {result.claudeChatUrl && (
                      <div className="flex items-center justify-between">
                        <span className="text-sm text-gray-600 dark:text-gray-400">Chat URL:</span>
                        <Button
                          onClick={() => window.open(result.claudeChatUrl, '_blank')}
                          variant="link"
                          size="sm"
                          className="p-0 h-auto"
                        >
                          {result.claudeChatUrl}
                        </Button>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

'use client';

import { useState } from 'react';
