'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Badge } from '@/components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { 
  FileText, 
  Download, 
  ExternalLink, 
  Loader2, 
  CheckCircle, 
  AlertTriangle,
  BarChart3,
  Target,
  TrendingUp,
  Zap,
  Globe,
  Calendar,
  Eye,
  Printer
} from 'lucide-react';

interface ExecutiveReport {
  reportId: string;
  markdownContent: string;
  htmlContent?: string;
  metadata: {
    url: string;
    generatedAt: string;
    frameworks: string[];
    dataSources: string[];
  };
}

interface ReportListItem {
  reportId: string;
  filename: string;
  createdAt: string;
  size: number;
}

export function ExecutiveReportViewer() {
  const [url, setUrl] = useState('');
  const [isGenerating, setIsGenerating] = useState(false);
  const [report, setReport] = useState<ExecutiveReport | null>(null);
  const [reports, setReports] = useState<ReportListItem[]>([]);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);

  // Load existing reports on component mount
  useEffect(() => {
    loadReports();
  }, []);

  const loadReports = async () => {
    try {
      const response = await fetch('/api/generate-executive-report');
      const data = await response.json();

      if (data.success) {
        setReports(data.reports || []);
      }
    } catch (error) {
      console.error('Failed to load reports:', error);
    }
  };

  const generateReport = async () => {
    if (!url.trim()) {
      setError('Please enter a valid URL');
      return;
    }

    setIsGenerating(true);
    setError(null);
    setSuccess(null);

    try {
      const response = await fetch('/api/generate-executive-report', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url: url.trim() }),
      });

      const data = await response.json();

      if (data.success) {
        setReport({
          reportId: data.reportId,
          markdownContent: data.markdownContent,
          htmlContent: data.htmlContent,
          metadata: data.metadata
        });
        setSuccess('Executive report generated successfully!');
        loadReports(); // Refresh the reports list
      } else {
        setError(data.error || 'Failed to generate report');
      }
    } catch (error) {
      setError('Network error. Please try again.');
      console.error('Report generation error:', error);
    } finally {
      setIsGenerating(false);
    }
  };

  const loadReport = async (reportId: string) => {
    try {
      const response = await fetch(`/api/generate-executive-report?reportId=${reportId}`);
      const data = await response.json();

      if (data.success) {
        setReport({
          reportId: data.reportId,
          markdownContent: data.markdownContent,
          htmlContent: data.htmlContent,
          metadata: {
            url: 'Previously generated report',
            generatedAt: data.metadata.createdAt,
            frameworks: [],
            dataSources: []
          }
        });
        setError(null);
        setSuccess('Report loaded successfully!');
      } else {
        setError(data.error || 'Failed to load report');
      }
    } catch (error) {
      setError('Failed to load report');
      console.error('Report loading error:', error);
    }
  };

  const downloadReport = (format: 'markdown' | 'html') => {
    if (!report) return;

    const content = format === 'html' ? report.htmlContent : report.markdownContent;
    const mimeType = format === 'html' ? 'text/html' : 'text/markdown';
    const extension = format === 'html' ? 'html' : 'md';

    const blob = new Blob([content || ''], { type: mimeType });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `executive-report-${report.reportId}.${extension}`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const printReport = () => {
    if (!report?.htmlContent) return;

    const printWindow = window.open('', '_blank');
    if (printWindow) {
      printWindow.document.write(report.htmlContent);
      printWindow.document.close();
      printWindow.print();
    }
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const formatDate = (dateString: string): string => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="text-center space-y-4">
        <div className="flex items-center justify-center gap-3">
          <FileText className="h-8 w-8 text-blue-600" />
          <h1 className="text-3xl font-bold text-gray-900">Executive Report Generator</h1>
        </div>
        <p className="text-lg text-gray-600 max-w-2xl mx-auto">
          Generate comprehensive executive reports with strategic insights, technical analysis, and actionable recommendations
        </p>
      </div>

      {/* Generation Form */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Target className="h-5 w-5 text-blue-600" />
            Generate New Report
          </CardTitle>
          <CardDescription>
            Enter a website URL to generate a comprehensive executive analysis report
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="url">Website URL</Label>
            <Input
              id="url"
              type="url"
              placeholder="https://example.com"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              disabled={isGenerating}
            />
          </div>

          <Button 
            onClick={generateReport} 
            disabled={isGenerating || !url.trim()}
            className="w-full"
          >
