/**
 * Google Tools Direct Access Page
 * Provides direct links to Google Tools and manual data input for analysis
 */

'use client';

import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { GoogleToolsDirectService } from '@/lib/services/google-tools-direct.service';
import { BarChart3, CheckCircle, Copy, ExternalLink, RefreshCw, Search, TrendingUp, X, Zap } from 'lucide-react';
import { useState } from 'react';

interface GoogleToolLink {
  name: string;
  url: string;
  description: string;
  icon: string;
}

export function GoogleToolsPage() {
  const [url, setUrl] = useState('');
  const [keywords, setKeywords] = useState('');
  const [toolLinks, setToolLinks] = useState<GoogleToolLink[]>([]);
  const [selectedTool, setSelectedTool] = useState<string | null>(null);
  const [manualData, setManualData] = useState('');
  const [analysisResult, setAnalysisResult] = useState<string | null>(null);
  const [copiedTool, setCopiedTool] = useState<string | null>(null);

  // Clear previous searches when URL changes
  const handleUrlChange = (newUrl: string) => {
    setUrl(newUrl);
    // Clear previous data when URL changes
    if (newUrl !== url) {
      setToolLinks([]);
      setSelectedTool(null);
      setManualData('');
      setAnalysisResult(null);
      setCopiedTool(null);
    }
  };

  const handleGenerateLinks = () => {
    if (!url.trim()) return;

    const keywordArray = keywords.split(',').map(k => k.trim()).filter(k => k.length > 0);
    const links = GoogleToolsDirectService.getToolLinks(url, keywordArray);
    setToolLinks(links);
  };

  const handleCopyLink = (toolName: string, url: string) => {
    navigator.clipboard.writeText(url);
    setCopiedTool(toolName);
    setTimeout(() => setCopiedTool(null), 2000);
  };

  const handleClearAll = () => {
    setUrl('');
    setKeywords('');
    setToolLinks([]);
    setSelectedTool(null);
    setManualData('');
    setAnalysisResult(null);
    setCopiedTool(null);
  };

  const handleAnalyzeData = async () => {
    if (!selectedTool || !manualData.trim()) return;

    try {
      const prompts = GoogleToolsDirectService.getPTCFPrompts();
      const prompt = prompts[selectedTool];

      if (!prompt) return;

      const fullPrompt = `${prompt.persona}

${prompt.task}

${prompt.context.replace('[PASTE', manualData)}

${prompt.format}`;

      // For now, just display the prompt - in production, this would send to Gemini
      setAnalysisResult(fullPrompt);
    } catch (error) {
      console.error('Analysis failed:', error);
    }
  };

  const getToolIcon = (toolName: string) => {
    switch (toolName) {
      case 'Google Trends': return <TrendingUp className="h-6 w-6" />;
      case 'PageSpeed Insights': return <Zap className="h-6 w-6" />;
      case 'Google Search Console': return <Search className="h-6 w-6" />;
      case 'Google Analytics': return <BarChart3 className="h-6 w-6" />;
      case 'Lighthouse Audit': return <Zap className="h-6 w-6" />;
      case 'GTmetrix Analysis': return <BarChart3 className="h-6 w-6" />;
      default: return <ExternalLink className="h-6 w-6" />;
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 dark:from-slate-900 dark:to-slate-800">
      <div className="container mx-auto py-8 px-4">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-4">
            Google Tools Analysis
          </h1>
          <p className="text-xl text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
            Access Google Tools directly and analyze the data using AI-powered insights
          </p>
        </div>

        {/* Input Form */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center">
              <ExternalLink className="mr-2 h-6 w-6 text-blue-600" />
              Generate Google Tools Links
            </CardTitle>
            <CardDescription>
              Enter your website URL and keywords to get direct links to useful Google Tools (PageSpeed + Trends)
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div>
                <Label htmlFor="url">Website URL</Label>
                <Input
                  id="url"
                  type="url"
                  placeholder="https://example.com"
                  value={url}
                  onChange={(e) => handleUrlChange(e.target.value)}
                  className="mt-1"
                />
              </div>
              <div>
                <Label htmlFor="keywords">Keywords (comma-separated)</Label>
                <Input
                  id="keywords"
                  type="text"
                  placeholder="custom homes, home builders, construction"
                  value={keywords}
                  onChange={(e) => setKeywords(e.target.value)}
                  className="mt-1"
                />
              </div>
              <div className="flex gap-2">
                <Button
                  onClick={handleGenerateLinks}
                  disabled={!url.trim()}
                  className="flex-1"
                >
                  <Search className="mr-2 h-4 w-4" />
                  Generate Google Tools Links
                </Button>
                <Button
                  onClick={handleClearAll}
                  variant="outline"
                  disabled={!url.trim() && !keywords.trim() && toolLinks.length === 0}
                >
                  <X className="mr-2 h-4 w-4" />
                  Clear All
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Google Tools Links */}
        {toolLinks.length > 0 && (
          <Card className="mb-8">
            <CardHeader>
              <div className="flex items-center justify-between">
                <div className="flex items-center">
                  <CheckCircle className="mr-2 h-6 w-6 text-green-600" />
                  <div>
                    <CardTitle>Google Tools Access</CardTitle>
                    <CardDescription>
                      Click on any tool to open it in a new tab, or copy the link to share
                    </CardDescription>
                  </div>
                </div>
                <Button
                  onClick={handleGenerateLinks}
                  variant="outline"
                  size="sm"
                >
                  <RefreshCw className="mr-2 h-4 w-4" />
                  Refresh Links
                </Button>
              </div>
            </CardHeader>
            <CardContent>
              <div className="grid gap-4 md:grid-cols-2">
                {toolLinks.map((tool, index) => (
                  <Card key={index} className="border-l-4 border-l-blue-500">
                    <CardContent className="pt-4">
                      <div className="flex items-center justify-between mb-2">
                        <div className="flex items-center space-x-2">
                          {getToolIcon(tool.name)}
                          <h3 className="font-semibold text-lg">{tool.name}</h3>
                        </div>
                        <div className="flex space-x-2">
                          <Button
                            size="sm"
                            variant="outline"
                            onClick={() => handleCopyLink(tool.name, tool.url)}
                          >
                            {copiedTool === tool.name ? (
                              <CheckCircle className="h-4 w-4 text-green-600" />
                            ) : (
                              <Copy className="h-4 w-4" />
                            )}
                          </Button>
                          <Button
                            size="sm"
                            onClick={() => window.open(tool.url, '_blank')}
                          >
                            <ExternalLink className="h-4 w-4" />
                          </Button>
                        </div>
                      </div>
                      <p className="text-sm text-gray-600 dark:text-gray-400 mb-2">
                        {tool.description}
                      </p>
                      <div className="text-xs text-gray-500 font-mono break-all">
                        {tool.url}
                      </div>
                    </CardContent>
                  </Card>
                ))}
              </div>
            </CardContent>
          </Card>
        )}

        {/* Manual Data Analysis */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center">
              <BarChart3 className="mr-2 h-6 w-6 text-purple-600" />
              Analyze Google Tools Data
            </CardTitle>
            <CardDescription>
              Paste data from any Google Tool to get AI-powered analysis
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div>
                <Label htmlFor="tool-select">Select Tool</Label>
                <select
                  id="tool-select"
                  value={selectedTool || ''}
                  onChange={(e) => setSelectedTool(e.target.value)}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                >
                  <option value="">Select a tool...</option>
                  <option value="trends">Google Trends</option>
                  <option value="analytics">Google Analytics</option>
                  <option value="searchConsole">Google Search Console</option>
                  <option value="pageSpeed">PageSpeed Insights</option>
                </select>
              </div>

              {selectedTool && (
                <div>
                  <Label htmlFor="manual-data">Paste Data from {selectedTool}</Label>
                  <Textarea
                    id="manual-data"
                    placeholder={GoogleToolsDirectService.getDataInputPrompts()[selectedTool]}
                    value={manualData}
                    onChange={(e) => setManualData(e.target.value)}
                    className="mt-1 min-h-[200px]"
                  />
                </div>
              )}

              <Button
                onClick={handleAnalyzeData}
                disabled={!selectedTool || !manualData.trim()}
                className="w-full"
              >
                Analyze Data with AI
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Analysis Results */}
        {analysisResult && (
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center">
                <CheckCircle className="mr-2 h-6 w-6 text-green-600" />
                Analysis Result
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="bg-gray-50 dark:bg-gray-800 p-4 rounded-lg">
                <pre className="whitespace-pre-wrap text-sm">
                  {analysisResult}
                </pre>
              </div>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

'use client';

import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { IndividualReport } from '@/lib/individual-report-generator';
import { Code, Download, Eye, FileText } from 'lucide-react';
import { useState } from 'react';

interface IndividualReportsViewProps {
  reports: IndividualReport[];
  url: string;
}

export function IndividualReportsView({ reports, url }: IndividualReportsViewProps) {
  const [selectedReport, setSelectedReport] = useState<IndividualReport | null>(null);
  const [viewMode, setViewMode] = useState<'preview' | 'markdown' | 'prompt'>('preview');

  const phase1Reports = reports.filter(r => r.phase === 'Phase 1');
  const phase2Reports = reports.filter(r => r.phase === 'Phase 2');
  const phase3Reports = reports.filter(r => r.phase === 'Phase 3');

  const downloadMarkdown = (report: IndividualReport) => {
    const blob = new Blob([report.markdown], { type: 'text/markdown' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${report.id}-${new Date().toISOString().split('T')[0]}.md`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const downloadPrompt = (report: IndividualReport) => {
    const blob = new Blob([report.prompt], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `${report.id}-prompt.txt`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(url);
  };

  const ReportCard = ({ report }: { report: IndividualReport }) => (
    <Card className="hover:shadow-lg transition-shadow">
      <CardHeader>
        <div className="flex items-start justify-between">
          <div className="flex-1">
            <CardTitle className="text-lg">{report.name}</CardTitle>
            <CardDescription>{report.phase}</CardDescription>
          </div>
          {report.score !== undefined && (
            <Badge variant={report.score >= 70 ? 'default' : 'destructive'}>
              {report.score}/100
            </Badge>
          )}
        </div>
      </CardHeader>
      <CardContent>
        <div className="flex flex-wrap gap-2">
          <Button
            size="sm"
            variant="outline"
            onClick={() => {
              setSelectedReport(report);
              setViewMode('preview');
            }}
          >
            <Eye className="mr-2 h-4 w-4" />
            View Report
          </Button>
          <Button
            size="sm"
            variant="outline"
            onClick={() => downloadMarkdown(report)}
          >
            <FileText className="mr-2 h-4 w-4" />
            Download .md
          </Button>
          {report.prompt !== 'N/A - Direct web scraping, no AI prompt' &&
           report.prompt !== 'N/A - Google Lighthouse automated tool' && (
            <Button
              size="sm"
              variant="outline"
              onClick={() => {
                setSelectedReport(report);
                setViewMode('prompt');
              }}
            >
              <Code className="mr-2 h-4 w-4" />
              View Prompt
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );

  return (
    <div className="space-y-6">
      {/* Reports by Phase */}
      <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
        {/* Phase 1 */}
        <div>
          <h3 className="text-lg font-semibold mb-3">Phase 1: Data Collection</h3>
          <div className="space-y-3">
            {phase1Reports.map(report => (
              <ReportCard key={report.id} report={report} />
            ))}
          </div>
        </div>

        {/* Phase 2 */}
        <div>
          <h3 className="text-lg font-semibold mb-3">Phase 2: Framework Analysis</h3>
          <div className="space-y-3">
            {phase2Reports.map(report => (
              <ReportCard key={report.id} report={report} />
            ))}
          </div>
        </div>

        {/* Phase 3 */}
        <div>
          <h3 className="text-lg font-semibold mb-3">Phase 3: Strategic Analysis</h3>
          <div className="space-y-3">
            {phase3Reports.map(report => (
              <ReportCard key={report.id} report={report} />
            ))}
          </div>
        </div>
      </div>

      {/* Selected Report Viewer */}
      {selectedReport && (
        <Card className="mt-6">
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle>{selectedReport.name}</CardTitle>
                <CardDescription>
                  {selectedReport.phase} â€¢ {new Date(selectedReport.timestamp).toLocaleString()}
                </CardDescription>
              </div>
              <Button
                variant="ghost"
                size="sm"
                onClick={() => setSelectedReport(null)}
              >
                Close
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            <Tabs value={viewMode} onValueChange={(v) => setViewMode(v as any)}>
              <TabsList className="mb-4">
                <TabsTrigger value="preview">Preview</TabsTrigger>
                <TabsTrigger value="markdown">Markdown</TabsTrigger>
                {selectedReport.prompt !== 'N/A - Direct web scraping, no AI prompt' &&
                 selectedReport.prompt !== 'N/A - Google Lighthouse automated tool' && (
                  <TabsTrigger value="prompt">AI Prompt</TabsTrigger>
                )}
              </TabsList>

              <TabsContent value="preview" className="space-y-4">
                <div className="prose dark:prose-invert max-w-none">
                  <div dangerouslySetInnerHTML={{ __html: markdownToHtml(selectedReport.markdown) }} />
                </div>
                <div className="flex gap-2 pt-4 border-t">
                  <Button onClick={() => downloadMarkdown(selectedReport)}>
                    <Download className="mr-2 h-4 w-4" />
                    Download Markdown
                  </Button>
                  {selectedReport.prompt !== 'N/A - Direct web scraping, no AI prompt' &&
                   selectedReport.prompt !== 'N/A - Google Lighthouse automated tool' && (
                    <Button variant="outline" onClick={() => downloadPrompt(selectedReport)}>
                      <Code className="mr-2 h-4 w-4" />
                      Download Prompt
                    </Button>
                  )}
                </div>
              </TabsContent>

              <TabsContent value="markdown">
                <pre className="bg-muted p-4 rounded-lg overflow-auto max-h-[600px] text-sm">
                  {selectedReport.markdown}
                </pre>
                <Button className="mt-4" onClick={() => downloadMarkdown(selectedReport)}>
                  <Download className="mr-2 h-4 w-4" />
                  Download Markdown
                </Button>
              </TabsContent>

              {selectedReport.prompt !== 'N/A - Direct web scraping, no AI prompt' &&
               selectedReport.prompt !== 'N/A - Google Lighthouse automated tool' && (
                <TabsContent value="prompt">
                  <div className="space-y-4">
                    <div className="bg-blue-50 dark:bg-blue-950 border border-blue-200 dark:border-blue-800 p-4 rounded-lg">
                      <p className="text-sm text-blue-900 dark:text-blue-100">
                        <strong>This is the exact prompt sent to Google Gemini AI.</strong> You can copy it and run it manually at{' '}
                        <a href="https://gemini.google.com/" target="_blank" rel="noopener noreferrer" className="underline">
                          gemini.google.com
                        </a>
                      </p>
                    </div>
                    <pre className="bg-muted p-4 rounded-lg overflow-auto max-h-[600px] text-sm whitespace-pre-wrap">
                      {selectedReport.prompt}
                    </pre>
                    <div className="flex gap-2">
                      <Button
                        onClick={() => {
                          navigator.clipboard.writeText(selectedReport.prompt);
                        }}
                      >
                        Copy Prompt
                      </Button>
                      <Button variant="outline" onClick={() => downloadPrompt(selectedReport)}>
                        <Download className="mr-2 h-4 w-4" />
                        Download Prompt
                      </Button>
                    </div>
                  </div>
                </TabsContent>
              )}
            </Tabs>
          </CardContent>
        </Card>
      )}

      {/* Download All Button */}
      <div className="flex justify-center pt-6">
        <Button
          size="lg"
          onClick={() => {
            reports.forEach(report => downloadMarkdown(report));
          }}
        >
          <Download className="mr-2 h-4 w-4" />
          Download All Reports ({reports.length})
        </Button>
      </div>
    </div>
  );
}

// Simple markdown to HTML converter (basic version)
function markdownToHtml(markdown: string): string {
  return markdown
    .replace(/^# (.+)$/gm, '<h1>$1</h1>')
    .replace(/^## (.+)$/gm, '<h2>$1</h2>')
    .replace(/^### (.+)$/gm, '<h3>$1</h3>')
    .replace(/^\*\*(.+?)\*\*$/gm, '<strong>$1</strong>')
    .replace(/\*\*(.+?)\*\*/g, '<strong>$1</strong>')
    .replace(/^- (.+)$/gm, '<li>$1</li>')
    .replace(/\n\n/g, '</p><p>')
    .replace(/^(.+)$/gm, '<p>$1</p>')
    .replace(/```([^`]+)```/g, '<pre><code>$1</code></pre>');
}

/**
 * Multi-Page Content Scraping Page
 * Allows users to scrape multiple pages from a website for comprehensive analysis
 */

'use client';

import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Checkbox } from '@/components/ui/checkbox';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
    CheckCircle,
    ExternalLink,
    FileText,
    Globe,
    Loader2,
    Search,
    Target,
    TrendingUp,
    Users,
    Zap
} from 'lucide-react';
import { useState } from 'react';

interface PageDiscoveryOptions {
  maxPages?: number;
  includeSubpages?: boolean;
  includeBlog?: boolean;
  includeProducts?: boolean;
  includeAbout?: boolean;
  includeContact?: boolean;
  includeServices?: boolean;
  maxDepth?: number;
}

interface MultiPageResult {
  homepage: any;
  additionalPages: Array<{
    url: string;
    data: any;
    pageType: string;
    priority: number;
  }>;
  siteMap: {
    totalPages: number;
    discoveredPages: string[];
    pageTypes: { [key: string]: number };
  };
  comprehensiveContent: {
    allText: string;
    allHeadings: string[];
    allKeywords: string[];
    contentThemes: string[];
  };
}

export function MultiPageScrapingPage() {
  const [url, setUrl] = useState('');
  const [isScraping, setIsScraping] = useState(false);
  const [result, setResult] = useState<MultiPageResult | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [options, setOptions] = useState<PageDiscoveryOptions>({
    maxPages: 10,
    includeSubpages: true,
    includeBlog: true,
    includeProducts: true,
    includeAbout: true,
    includeContact: true,
    includeServices: true,
    maxDepth: 2
  });

  const handleScrape = async () => {
    if (!url.trim()) {
      setError('Please enter a website URL');
      return;
    }

    setIsScraping(true);
    setError(null);
    setResult(null);

    try {
      const response = await fetch('/api/scrape-multi-page', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url, options }),
      });

      const data = await response.json();

      if (data.success) {
        setResult(data.data);
        console.log('Multi-page scraping completed:', data.summary);
      } else {
        setError(data.error || 'Multi-page scraping failed');
      }
    } catch (error) {
      setError(error instanceof Error ? error.message : 'Multi-page scraping failed');
    } finally {
      setIsScraping(false);
    }
  };

  const handleOptionChange = (key: keyof PageDiscoveryOptions, value: any) => {
    setOptions(prev => ({
      ...prev,
      [key]: value
    }));
  };

  return (
    <div className="max-w-6xl mx-auto p-6 space-y-6">
      <div className="text-center space-y-4">
        <h1 className="text-3xl font-bold flex items-center justify-center">
          <Globe className="mr-3 h-8 w-8 text-blue-600" />
          Multi-Page Content Scraping
        </h1>
        <p className="text-lg text-muted-foreground max-w-2xl mx-auto">
          Discover and scrape content from multiple pages across a website for comprehensive analysis
        </p>
      </div>

      {/* URL Input */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Target className="mr-2 h-6 w-6 text-blue-600" />
            Website URL
          </CardTitle>
          <CardDescription>
            Enter the website URL to discover and scrape multiple pages
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div>
              <Label htmlFor="url">Website URL</Label>
              <Input
                id="url"
                type="url"
                placeholder="https://example.com"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                disabled={isScraping}
                className="mt-1"
              />
            </div>

            <Button
              onClick={handleScrape}
              disabled={isScraping || !url.trim()}
              className="w-full"
            >
              {isScraping ? (
                <>
                  <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                  Scraping Multiple Pages...
                </>
              ) : (
                <>
                  <Search className="mr-2 h-4 w-4" />
                  Discover & Scrape Pages
                </>
              )}
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Scraping Options */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center">
            <Zap className="mr-2 h-6 w-6 text-green-600" />
            Scraping Options
          </CardTitle>
          <CardDescription>
            Configure which types of pages to discover and scrape
          </CardDescription>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="space-y-4">
              <div>
                <Label htmlFor="maxPages">Maximum Pages</Label>
                <Input
                  id="maxPages"
                  type="number"
                  min="1"
                  max="20"
                  value={options.maxPages}
                  onChange={(e) => handleOptionChange('maxPages', parseInt(e.target.value))}
                  disabled={isScraping}
                  className="mt-1"
                />
              </div>

              <div>
                <Label htmlFor="maxDepth">Maximum Depth</Label>
                <Input
                  id="maxDepth"
                  type="number"
                  min="1"
                  max="5"
                  value={options.maxDepth}
                  onChange={(e) => handleOptionChange('maxDepth', parseInt(e.target.value))}
                  disabled={isScraping}
                  className="mt-1"
                />
              </div>
            </div>

            <div className="space-y-4">
              <div className="flex items-center space-x-2">
                <Checkbox
                  id="includeSubpages"
                  checked={options.includeSubpages}
