'use client';

import { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Loader2, Brain } from 'lucide-react';

interface Phase2ButtonProps {
  scrapedContent: any;
  url: string;
  industry?: string;
  onPhase2Complete: (result: any) => void;
}

export function Phase2Button({ scrapedContent, url, industry, onPhase2Complete }: Phase2ButtonProps) {
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const handlePhase2 = async () => {
    setIsAnalyzing(true);
    setError(null);

    try {
      const response = await fetch('/api/analyze/phase2-simple', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          url,
          content: scrapedContent,
          industry: industry || 'general'
        }),
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error || 'Phase 2 analysis failed');
      }

      console.log('Phase 2 Analysis Complete:', result);

      // Create complete analysis result
      const completeAnalysis = {
        id: `analysis-${Date.now()}`,
        url: result.url,
        overallScore: 75, // Placeholder score
        summary: 'Phase 2 AI analysis completed',
        status: 'phase2_complete' as const,
        timestamp: new Date().toISOString(),
        goldenCircle: result.data.goldenCircle,
        elementsOfValue: result.data.elementsOfValue,
        cliftonStrengths: result.data.cliftonStrengths,
        b2bElements: result.data.b2bElements,
        recommendations: [],
        scrapedContent: scrapedContent,
        phase: 2
      };

      onPhase2Complete(completeAnalysis);

    } catch (err) {
      console.error('Phase 2 analysis error:', err);
      setError(err instanceof Error ? err.message : 'Unknown error');
    } finally {
      setIsAnalyzing(false);
    }
  };

  return (
    <div className="space-y-4">
      <Button
        onClick={handlePhase2}
        disabled={isAnalyzing}
        className="w-full"
        size="lg"
      >
        {isAnalyzing ? (
          <>
            <Loader2 className="mr-2 h-4 w-4 animate-spin" />
            Running AI Analysis...
          </>
        ) : (
          <>
            <Brain className="mr-2 h-4 w-4" />
            Run Phase 2: AI Analysis
          </>
        )}
      </Button>

      {error && (
        <div className="text-red-600 text-sm">
          Error: {error}
        </div>
      )}
    </div>
  );
}

'use client';

import { Alert, AlertDescription } from '@/components/ui/alert';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { ArrowRight, CheckCircle, Loader2, Play } from 'lucide-react';
import { useState } from 'react';
import { ContentPreviewCard } from './ContentPreviewCard';
import { IndividualReportsView } from './IndividualReportsView';

export function PhasedAnalysisPage() {
  const [url, setUrl] = useState('');
  const [analysisId, setAnalysisId] = useState<string | null>(null);
  const [currentPhase, setCurrentPhase] = useState(0);
  const [isRunning, setIsRunning] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const [phase1Reports, setPhase1Reports] = useState<any[]>([]);
  const [phase2Reports, setPhase2Reports] = useState<any[]>([]);
  const [phase3Reports, setPhase3Reports] = useState<any[]>([]);

  const [phase1Data, setPhase1Data] = useState<any>(null);
  const [phase2Data, setPhase2Data] = useState<any>(null);
  const [phase3Data, setPhase3Data] = useState<any>(null);

  const runPhase = async (phase: number) => {
    setIsRunning(true);
    setError(null);

    try {
      const response = await fetch('/api/analyze/phase', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          url: url.trim(),
          phase,
          analysisId: analysisId
        })
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Phase execution failed');
      }

      if (data.success) {
        setAnalysisId(data.analysisId);
        setCurrentPhase(phase);

        if (phase === 1) {
          setPhase1Data(data.data);
          setPhase1Reports(data.individualReports || []);
        } else if (phase === 2) {
          setPhase2Data(data.data);
          setPhase2Reports(data.individualReports.filter((r: any) => r.phase === 'Phase 2'));

          // Show recommendations if Phase 1 was skipped
          if (data.recommendations && data.recommendations.length > 0) {
            setError(null); // Clear error
            // Recommendations will be shown in the reports
          }
        } else if (phase === 3) {
          setPhase3Data(data.data);
          setPhase3Reports(data.individualReports.filter((r: any) => r.phase === 'Phase 3'));

          // Show recommendations if prior phases were skipped
          if (data.recommendations && data.recommendations.length > 0) {
            setError(null);
          }
        }
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to run phase');
    } finally {
      setIsRunning(false);
    }
  };

  return (
    <div className="max-w-7xl mx-auto p-6 space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="text-2xl">Phased Website Analysis</CardTitle>
          <CardDescription>
            Run each phase separately. Review results before proceeding to the next phase.
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex gap-2">
            <Input
              type="url"
              placeholder="Enter website URL (e.g., https://example.com)"
              value={url}
              onChange={(e) => setUrl(e.target.value)}
              disabled={currentPhase > 0}
              className="flex-1"
            />
          </div>

          {error && (
            <Alert variant="destructive">
              <AlertDescription>{error}</AlertDescription>
            </Alert>
          )}

          {/* Phase Progress */}
          <div className="grid grid-cols-3 gap-4 mt-6">
            {/* Phase 1 */}
            <Card className={currentPhase >= 1 ? 'border-green-500' : ''}>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <CardTitle className="text-lg">Phase 1</CardTitle>
                  {currentPhase >= 1 && <CheckCircle className="h-5 w-5 text-green-500" />}
                </div>
                <CardDescription>Data Collection</CardDescription>
              </CardHeader>
              <CardContent>
                <ul className="text-sm space-y-1 text-muted-foreground mb-3">
                  <li>â€¢ Collect website content & metadata</li>
                  <li>â€¢ Extract keywords & topics</li>
                  <li>â€¢ Prepare data for AI analysis</li>
                </ul>
                <Button
                  onClick={() => runPhase(1)}
                  disabled={!url.trim() || isRunning || currentPhase >= 1}
                  className="w-full"
                >
                  {isRunning && currentPhase === 0 ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      Running...
                    </>
                  ) : currentPhase >= 1 ? (
                    <>
                      <CheckCircle className="mr-2 h-4 w-4" />
                      Complete
                    </>
                  ) : (
                    <>
                      <Play className="mr-2 h-4 w-4" />
                      Start Phase 1
                    </>
                  )}
                </Button>
              </CardContent>
            </Card>

            {/* Phase 2 */}
            <Card className={currentPhase >= 2 ? 'border-green-500' : currentPhase === 1 ? 'border-blue-500' : ''}>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <CardTitle className="text-lg">Phase 2</CardTitle>
                  {currentPhase >= 2 && <CheckCircle className="h-5 w-5 text-green-500" />}
                </div>
                <CardDescription>Framework Analysis</CardDescription>
              </CardHeader>
              <CardContent>
                <ul className="text-sm space-y-1 text-muted-foreground mb-3">
                  <li>â€¢ Golden Circle (Gemini AI)</li>
                  <li>â€¢ Elements of Value (Gemini AI)</li>
                  <li>â€¢ B2B Elements (Gemini AI)</li>
                  <li>â€¢ CliftonStrengths (Gemini AI)</li>
                </ul>
                {currentPhase === 1 && (
                  <div className="mb-3 p-2 bg-green-50 dark:bg-green-950 border border-green-200 rounded text-xs text-green-800 dark:text-green-200">
                    âœ… Ready! Will analyze content from Phase 1
                  </div>
                )}
                <Button
                  onClick={() => runPhase(2)}
                  disabled={isRunning || currentPhase >= 2}
                  className="w-full"
                  variant={currentPhase === 1 ? 'default' : 'outline'}
                >
                  {isRunning && currentPhase === 1 ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      Running...
                    </>
                  ) : currentPhase >= 2 ? (
                    <>
                      <CheckCircle className="mr-2 h-4 w-4" />
                      Complete
                    </>
                  ) : currentPhase === 1 ? (
                    <>
                      <ArrowRight className="mr-2 h-4 w-4" />
                      Start Phase 2
                    </>
                  ) : (
                    <>
                      <Play className="mr-2 h-4 w-4" />
                      Start Phase 2 (Standalone)
                    </>
                  )}
                </Button>
              </CardContent>
            </Card>

            {/* Phase 3 */}
            <Card className={currentPhase >= 3 ? 'border-green-500' : currentPhase === 2 ? 'border-blue-500' : ''}>
              <CardHeader>
                <div className="flex items-center justify-between">
                  <CardTitle className="text-lg">Phase 3</CardTitle>
                  {currentPhase >= 3 && <CheckCircle className="h-5 w-5 text-green-500" />}
                </div>
                <CardDescription>Strategic Analysis</CardDescription>
              </CardHeader>
              <CardContent>
                <ul className="text-sm space-y-1 text-muted-foreground mb-3">
                  <li>â€¢ Comprehensive insights (Gemini AI)</li>
                  <li>â€¢ Priority recommendations</li>
                  <li>â€¢ Quick wins & long-term strategy</li>
                </ul>
                <Button
                  onClick={() => runPhase(3)}
                  disabled={currentPhase < 2 || isRunning || currentPhase >= 3}
                  className="w-full"
                  variant={currentPhase === 2 ? 'default' : 'outline'}
                >
                  {isRunning && currentPhase === 2 ? (
                    <>
                      <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                      Running...
                    </>
                  ) : currentPhase >= 3 ? (
                    <>
                      <CheckCircle className="mr-2 h-4 w-4" />
                      Complete
                    </>
                  ) : currentPhase === 2 ? (
                    <>
                      <ArrowRight className="mr-2 h-4 w-4" />
                      Start Phase 3
                    </>
                  ) : (
                    'Complete Phase 2 First'
                  )}
                </Button>
              </CardContent>
            </Card>
          </div>
        </CardContent>
      </Card>

      {/* Phase 1 Results */}
      {phase1Reports.length > 0 && (
        <>
          {/* Content Preview - Show What Was Collected */}
          {phase1Data?.scrapedContent && (
            <Card className="border-2 border-blue-500">
              <CardHeader>
                <div className="flex items-center justify-between">
                  <div>
                    <CardTitle className="text-blue-900 dark:text-blue-100">
                      âœ… Content Successfully Collected
                    </CardTitle>
                    <CardDescription>
                      Review the content, meta tags, and keywords we extracted. This is what AI will analyze in Phase 2.
                    </CardDescription>
                  </div>
                  <Badge variant="default" className="bg-blue-500">
                    Foundation Data
                  </Badge>
                </div>
              </CardHeader>
              <CardContent>
                <ContentPreviewCard data={phase1Data.scrapedContent} />
              </CardContent>
            </Card>
          )}

          {/* Individual Reports */}
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <div>
                  <CardTitle>Phase 1: Data Collection Reports</CardTitle>
                  <CardDescription>
                    {phase1Reports.length} reports â€¢ Download individually or all at once
                  </CardDescription>
                </div>
                <Badge variant="default" className="bg-green-500">
                  âœ“ Phase 1 Complete
                </Badge>
              </div>
            </CardHeader>
            <CardContent>
              <IndividualReportsView reports={phase1Reports} url={url} />
            </CardContent>
          </Card>
        </>
      )}

      {/* Phase 2 Results */}
      {phase2Reports.length > 0 && (
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle>Phase 2: Framework Analysis Results</CardTitle>
                <CardDescription>
                  {phase2Reports.length} AI-powered framework analyses
                </CardDescription>
              </div>
              <Badge variant="default" className="bg-green-500">
                âœ“ Phase 2 Complete
              </Badge>
            </div>
          </CardHeader>
          <CardContent>
            <IndividualReportsView reports={phase2Reports} url={url} />
          </CardContent>
        </Card>
      )}

      {/* Phase 3 Results */}
      {phase3Reports.length > 0 && (
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <div>
                <CardTitle>Phase 3: Strategic Analysis Results</CardTitle>
                <CardDescription>
                  Comprehensive insights and recommendations
                </CardDescription>
              </div>
              <Badge variant="default" className="bg-green-500">
                âœ“ Phase 3 Complete
              </Badge>
            </div>
          </CardHeader>
          <CardContent>
            <IndividualReportsView reports={phase3Reports} url={url} />

            {phase3Data && (
              <div className="mt-6 p-4 bg-green-50 dark:bg-green-950 border border-green-200 rounded-lg">
                <h3 className="text-lg font-semibold text-green-900 dark:text-green-100 mb-2">
                  ðŸŽ‰ All Phases Complete!
                </h3>
                <p className="text-sm text-green-700 dark:text-green-300 mb-4">
                  Your comprehensive website analysis is ready. You have {phase1Reports.length + phase2Reports.length + phase3Reports.length} individual reports to review.
                </p>
                <div className="flex gap-2">
                  <Button onClick={() => {
                    // Download all reports logic
                    [...phase1Reports, ...phase2Reports, ...phase3Reports].forEach(report => {
                      const blob = new Blob([report.markdown], { type: 'text/markdown' });
                      const url = URL.createObjectURL(blob);
                      const a = document.createElement('a');
                      a.href = url;
                      a.download = `${report.id}-${new Date().toISOString().split('T')[0]}.md`;
                      document.body.appendChild(a);
                      a.click();
                      document.body.removeChild(a);
                      URL.revokeObjectURL(url);
                    });
                  }}>
                    Download All Reports
                  </Button>
                </div>
              </div>
            )}
          </CardContent>
        </Card>
      )}
    </div>
  );
}

'use client';

import React from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { CheckCircle, Clock, AlertCircle, Play, Pause, Loader2 } from 'lucide-react';

export interface MiniDeliverable {
  id: string;
  title: string;
  description: string;
  status: 'pending' | 'running' | 'completed' | 'failed';
  progress: number; // 0-100
  duration?: string;
  result?: any;
  error?: string;
  timestamp?: string;
}

export interface PhaseProgress {
  id: string;
  name: string;
  description: string;
  status: 'pending' | 'running' | 'completed' | 'failed';
  progress: number; // 0-100
  deliverables: MiniDeliverable[];
  startTime?: string;
  endTime?: string;
  duration?: string;
}

export interface AnalysisProgress {
  id: string;
  url: string;
  overallProgress: number; // 0-100
  currentPhase: string;
  status: 'pending' | 'running' | 'completed' | 'failed' | 'paused';
  phases: PhaseProgress[];
  startTime: string;
  endTime?: string;
  totalDuration?: string;
}

interface ProgressTrackerProps {
  progress: AnalysisProgress;
  onPhaseClick?: (phaseId: string) => void;
  onDeliverableClick?: (phaseId: string, deliverableId: string) => void;
  showDetails?: boolean;
  compact?: boolean;
}

export function ProgressTracker({ 
  progress, 
  onPhaseClick, 
  onDeliverableClick, 
  showDetails = true,
  compact = false 
}: ProgressTrackerProps) {
  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'completed':
        return <CheckCircle className="h-4 w-4 text-green-500" />;
      case 'running':
        return <Loader2 className="h-4 w-4 text-blue-500 animate-spin" />;
      case 'failed':
        return <AlertCircle className="h-4 w-4 text-red-500" />;
      case 'paused':
        return <Pause className="h-4 w-4 text-yellow-500" />;
      default:
        return <Clock className="h-4 w-4 text-gray-400" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'bg-green-500';
      case 'running':
        return 'bg-blue-500';
      case 'failed':
        return 'bg-red-500';
      case 'paused':
        return 'bg-yellow-500';
      default:
        return 'bg-gray-300';
    }
  };

  const getStatusBadge = (status: string) => {
    const colors = {
      completed: 'bg-green-100 text-green-800 border-green-200',
      running: 'bg-blue-100 text-blue-800 border-blue-200',
      failed: 'bg-red-100 text-red-800 border-red-200',
      paused: 'bg-yellow-100 text-yellow-800 border-yellow-200',
      pending: 'bg-gray-100 text-gray-800 border-gray-200'
    };

    return (
      <Badge className={`${colors[status as keyof typeof colors] || colors.pending} border`}>
        {status.charAt(0).toUpperCase() + status.slice(1)}
      </Badge>
    );
  };

  if (compact) {
    return (
      <Card className="w-full">
        <CardHeader className="pb-3">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2">
              <CardTitle className="text-lg">Analysis Progress</CardTitle>
              {getStatusIcon(progress.status)}
            </div>
            <div className="text-right">
              <div className="text-2xl font-bold text-blue-600">{progress.overallProgress}%</div>
              <div className="text-sm text-gray-500">{progress.currentPhase}</div>
            </div>
          </div>
          <Progress value={progress.overallProgress} className="w-full" />
        </CardHeader>
      </Card>
    );
  }

  return (
    <Card className="w-full">
      <CardHeader>
        <div className="flex items-center justify-between">
          <div>
            <CardTitle className="text-xl">Analysis Progress</CardTitle>
            <CardDescription>
              Analyzing {progress.url} â€¢ Started {new Date(progress.startTime).toLocaleTimeString()}
            </CardDescription>
          </div>
          <div className="text-right">
            <div className="text-3xl font-bold text-blue-600">{progress.overallProgress}%</div>
            <div className="text-sm text-gray-500">Overall Progress</div>
          </div>
        </div>
        <Progress value={progress.overallProgress} className="w-full h-3" />
      </CardHeader>

      <CardContent className="space-y-4">
        {/* Overall Status */}
        <div className="flex items-center justify-between p-3 bg-gray-50 rounded-lg">
          <div className="flex items-center space-x-3">
            {getStatusIcon(progress.status)}
            <div>
              <div className="font-medium">Current Status</div>
              <div className="text-sm text-gray-600">{progress.currentPhase}</div>
            </div>
          </div>
          {getStatusBadge(progress.status)}
        </div>

        {/* Phases */}
        <div className="space-y-3">
          <h3 className="font-semibold text-lg">Analysis Phases</h3>
          {progress.phases.map((phase) => (
            <Card 
              key={phase.id} 
              className={`cursor-pointer transition-all hover:shadow-md ${
                phase.status === 'running' ? 'ring-2 ring-blue-500' : ''
              }`}
              onClick={() => onPhaseClick?.(phase.id)}
            >
              <CardHeader className="pb-2">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    {getStatusIcon(phase.status)}
                    <div>
                      <CardTitle className="text-base">{phase.name}</CardTitle>
                      <CardDescription className="text-sm">{phase.description}</CardDescription>
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="text-lg font-semibold">{phase.progress}%</div>
                    {getStatusBadge(phase.status)}
                  </div>
                </div>
                <Progress value={phase.progress} className="w-full h-2" />
              </CardHeader>

              {showDetails && phase.deliverables.length > 0 && (
                <CardContent className="pt-0">
                  <div className="space-y-2">
                    <h4 className="text-sm font-medium text-gray-700">Mini Deliverables:</h4>
                    {phase.deliverables.map((deliverable) => (
                      <div 
                        key={deliverable.id}
                        className="flex items-center justify-between p-2 bg-gray-50 rounded cursor-pointer hover:bg-gray-100 transition-colors"
                        onClick={(e) => {
                          e.stopPropagation();
                          onDeliverableClick?.(phase.id, deliverable.id);
                        }}
                      >
                        <div className="flex items-center space-x-2">
                          {getStatusIcon(deliverable.status)}
                          <div>
                            <div className="text-sm font-medium">{deliverable.title}</div>
                            <div className="text-xs text-gray-600">{deliverable.description}</div>
                          </div>
                        </div>
                        <div className="text-right">
                          <div className="text-sm font-medium">{deliverable.progress}%</div>
                          {deliverable.duration && (
                            <div className="text-xs text-gray-500">{deliverable.duration}</div>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              )}
            </Card>
          ))}
        </div>

        {/* Timing Information */}
        <div className="grid grid-cols-2 gap-4 pt-4 border-t">
          <div>
            <div className="text-sm font-medium text-gray-700">Start Time</div>
            <div className="text-sm text-gray-600">
              {new Date(progress.startTime).toLocaleString()}
            </div>
          </div>
          {progress.endTime && (
            <div>
              <div className="text-sm font-medium text-gray-700">End Time</div>
              <div className="text-sm text-gray-600">
                {new Date(progress.endTime).toLocaleString()}
              </div>
            </div>
          )}
          {progress.totalDuration && (
            <div className="col-span-2">
              <div className="text-sm font-medium text-gray-700">Total Duration</div>
              <div className="text-sm text-gray-600">{progress.totalDuration}</div>
            </div>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

