                <CardTitle className="flex items-center">
                  <BarChart3 className="mr-2 h-6 w-6 text-red-600" />
                  Competitive Analysis
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <div>
                    <h4 className="text-lg font-semibold mb-3">Market Leaders</h4>
                    <div className="flex flex-wrap gap-2">
                      {result.competitive_analysis.market_leaders.map((leader, index) => (
                        <Badge key={index} variant="outline">{leader}</Badge>
                      ))}
                    </div>
                  </div>

                  <div>
                    <h4 className="text-lg font-semibold mb-3">Content Gaps</h4>
                    <div className="flex flex-wrap gap-2">
                      {result.competitive_analysis.content_gaps.map((gap, index) => (
                        <Badge key={index} variant="outline" className="bg-yellow-100 text-yellow-800">
                          {gap}
                        </Badge>
                      ))}
                    </div>
                  </div>

                  <div>
                    <h4 className="text-lg font-semibold mb-3">Differentiation Opportunities</h4>
                    <div className="flex flex-wrap gap-2">
                      {result.competitive_analysis.differentiation_opportunities.map((opp, index) => (
                        <Badge key={index} variant="outline" className="bg-green-100 text-green-800">
                          {opp}
                        </Badge>
                      ))}
                    </div>
                  </div>

                  <div>
                    <h4 className="text-lg font-semibold mb-3">Pricing Opportunities</h4>
                    <div className="flex flex-wrap gap-2">
                      {result.competitive_analysis.pricing_opportunities.map((opp, index) => (
                        <Badge key={index} variant="outline" className="bg-blue-100 text-blue-800">
                          {opp}
                        </Badge>
                      ))}
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        )}
      </div>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Checkbox } from '@/components/ui/checkbox';
import { SEOAnalysis } from '@/types/analysis';
import SEOAnalysisResults from './SEOAnalysisResults';
import { Search, Loader2, AlertCircle, CheckCircle2 } from 'lucide-react';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { AnalysisClient } from '@/lib/analysis-client';

export default function SEOAnalysisForm() {
  const [url, setUrl] = useState('');
  const [targetKeywords, setTargetKeywords] = useState('');
  const [competitorUrls, setCompetitorUrls] = useState('');
  const [includeSearchConsole, setIncludeSearchConsole] = useState(true);
  const [includeKeywordResearch, setIncludeKeywordResearch] = useState(true);
  const [includeCompetitiveAnalysis, setIncludeCompetitiveAnalysis] = useState(true);

  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [analysis, setAnalysis] = useState<SEOAnalysis | null>(null);
  const [timestamp, setTimestamp] = useState('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setAnalysis(null);

    try {
      // Parse target keywords
      const keywords = targetKeywords
        .split(',')
        .map(k => k.trim())
        .filter(k => k.length > 0);

      // Parse competitor URLs
      const competitors = competitorUrls
        .split(',')
        .map(c => c.trim())
        .filter(c => c.length > 0);

      const response = await fetch('/api/analyze/seo', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          url,
          targetKeywords: keywords.length > 0 ? keywords : undefined,
          competitorUrls: competitors.length > 0 ? competitors : undefined,
          includeSearchConsole,
          includeKeywordResearch,
          includeCompetitiveAnalysis,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'SEO analysis failed');
      }

      // Save to localStorage
      try {
        const analysisForStorage = {
          id: Date.now().toString(),
          url: url,
          overallScore: 75, // SEO analyses don't have overall scores, using default
          summary: 'SEO analysis completed',
          status: 'completed' as const,
          timestamp: data.timestamp || new Date().toISOString(),
          goldenCircle: { why: '', how: '', what: '', overallScore: 0, insights: [] },
          elementsOfValue: { functional: {}, emotional: {}, lifeChanging: {}, socialImpact: {}, overallScore: 0, insights: [] },
          cliftonStrengths: { themes: [], recommendations: [], overallScore: 0, insights: [] },
          recommendations: []
        };

        AnalysisClient.saveAnalysis(analysisForStorage);
        console.log('âœ… SEO analysis saved to localStorage');
      } catch (saveError) {
        console.error('Failed to save SEO analysis:', saveError);
      }

      setAnalysis(data.data);
      setTimestamp(data.timestamp);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'SEO analysis failed');
    } finally {
      setLoading(false);
    }
  };

  const handleQuickAnalysis = async () => {
    if (!url) return;

    setLoading(true);
    setError(null);
    setAnalysis(null);

    try {
      const response = await fetch(`/api/analyze/seo?url=${encodeURIComponent(url)}`);
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Quick SEO analysis failed');
      }

      // Save to localStorage
      try {
        const analysisForStorage = {
          id: Date.now().toString(),
          url: url,
          overallScore: 75,
          summary: 'Quick SEO analysis completed',
          status: 'completed' as const,
          timestamp: data.timestamp || new Date().toISOString(),
          goldenCircle: { why: '', how: '', what: '', overallScore: 0, insights: [] },
          elementsOfValue: { functional: {}, emotional: {}, lifeChanging: {}, socialImpact: {}, overallScore: 0, insights: [] },
          cliftonStrengths: { themes: [], recommendations: [], overallScore: 0, insights: [] },
          recommendations: []
        };

        AnalysisClient.saveAnalysis(analysisForStorage);
        console.log('âœ… Quick SEO analysis saved to localStorage');
      } catch (saveError) {
        console.error('Failed to save quick SEO analysis:', saveError);
      }

      setAnalysis(data.data);
      setTimestamp(data.timestamp);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Quick SEO analysis failed');
    } finally {
      setLoading(false);
    }
  };

  if (analysis) {
    return <SEOAnalysisResults analysis={analysis} url={url} timestamp={timestamp} />;
  }

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Search className="h-6 w-6 text-blue-600" />
            SEO Analysis Configuration
          </CardTitle>
          <CardDescription>
            Configure your SEO analysis following the practical workflow:
            <br />
            <strong>Search Console</strong> â†’ <strong>Keyword Planner</strong> â†’ <strong>Google Trends</strong> â†’ <strong>Competitive Analysis</strong>
          </CardDescription>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-6">
            {/* URL Input */}
            <div className="space-y-2">
              <Label htmlFor="url">Website URL *</Label>
              <Input
                id="url"
                type="url"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                placeholder="https://example.com"
                required
                className="w-full"
              />
              <p className="text-sm text-gray-500">
                Enter the website URL you want to analyze for SEO performance
              </p>
            </div>

            {/* Target Keywords */}
            <div className="space-y-2">
              <Label htmlFor="keywords">Target Keywords (Optional)</Label>
              <Textarea
                id="keywords"
                value={targetKeywords}
                onChange={(e) => setTargetKeywords(e.target.value)}
                placeholder="salesforce consulting, crm implementation, business automation"
                className="w-full"
                rows={3}
              />
              <p className="text-sm text-gray-500">
                Comma-separated list of keywords you want to analyze (leave empty for automatic discovery)
              </p>
            </div>

            {/* Competitor URLs */}
            <div className="space-y-2">
              <Label htmlFor="competitors">Competitor URLs (Optional)</Label>
              <Textarea
                id="competitors"
                value={competitorUrls}
                onChange={(e) => setCompetitorUrls(e.target.value)}
                placeholder="https://competitor1.com, https://competitor2.com"
                className="w-full"
                rows={3}
              />
              <p className="text-sm text-gray-500">
                Comma-separated list of competitor websites to compare against
              </p>
            </div>

            {/* Analysis Options */}
            <div className="space-y-4">
              <Label className="text-base font-medium">Analysis Components</Label>
              <div className="space-y-3">
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="search-console"
                    checked={includeSearchConsole}
                    onCheckedChange={(checked) => setIncludeSearchConsole(checked as boolean)}
                  />
                  <Label htmlFor="search-console" className="text-sm font-normal">
                    Search Console Analysis - Current keyword rankings and performance
                  </Label>
                </div>
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="keyword-research"
                    checked={includeKeywordResearch}
                    onCheckedChange={(checked) => setIncludeKeywordResearch(checked as boolean)}
                  />
                  <Label htmlFor="keyword-research" className="text-sm font-normal">
                    Keyword Research - Search volume and opportunity analysis
                  </Label>
                </div>
                <div className="flex items-center space-x-2">
                  <Checkbox
                    id="competitive-analysis"
                    checked={includeCompetitiveAnalysis}
                    onCheckedChange={(checked) => setIncludeCompetitiveAnalysis(checked as boolean)}
                  />
                  <Label htmlFor="competitive-analysis" className="text-sm font-normal">
                    Competitive Analysis - Compare against reference sites
                  </Label>
                </div>
              </div>
            </div>

            {/* Error Display */}
            {error && (
              <Alert variant="destructive">
                <AlertCircle className="h-4 w-4" />
                <AlertDescription>{error}</AlertDescription>
              </Alert>
            )}

            {/* Action Buttons */}
            <div className="flex gap-4">
              <Button
                type="submit"
                disabled={loading || !url}
                className="flex-1"
              >
                {loading ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Analyzing SEO...
                  </>
                ) : (
                  <>
                    <Search className="mr-2 h-4 w-4" />
                    Run Complete SEO Analysis
                  </>
                )}
              </Button>

              <Button
                type="button"
                variant="outline"
                onClick={handleQuickAnalysis}
                disabled={loading || !url}
              >
                {loading ? (
                  <Loader2 className="h-4 w-4 animate-spin" />
                ) : (
                  <>
                    <Search className="mr-2 h-4 w-4" />
                    Quick Analysis
                  </>
                )}
              </Button>
            </div>
          </form>
        </CardContent>
      </Card>

      {/* Analysis Workflow Information */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <CheckCircle2 className="h-5 w-5 text-green-600" />
            SEO Analysis Workflow
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <div className="text-center p-4 border rounded-lg">
              <div className="w-8 h-8 bg-blue-100 text-blue-600 rounded-full flex items-center justify-center mx-auto mb-2 text-sm font-bold">
                1
              </div>
              <h4 className="font-semibold text-sm">Search Console</h4>
              <p className="text-xs text-gray-600 mt-1">
                Analyze current keyword rankings and performance metrics
              </p>
            </div>

            <div className="text-center p-4 border rounded-lg">
              <div className="w-8 h-8 bg-green-100 text-green-600 rounded-full flex items-center justify-center mx-auto mb-2 text-sm font-bold">
                2
              </div>
              <h4 className="font-semibold text-sm">Keyword Research</h4>
              <p className="text-xs text-gray-600 mt-1">
                Research search volume and identify new opportunities
              </p>
            </div>

            <div className="text-center p-4 border rounded-lg">
              <div className="w-8 h-8 bg-purple-100 text-purple-600 rounded-full flex items-center justify-center mx-auto mb-2 text-sm font-bold">
                3
              </div>
              <h4 className="font-semibold text-sm">Google Trends</h4>
              <p className="text-xs text-gray-600 mt-1">
                Validate keyword trends and industry direction
              </p>
            </div>

            <div className="text-center p-4 border rounded-lg">
              <div className="w-8 h-8 bg-red-100 text-red-600 rounded-full flex items-center justify-center mx-auto mb-2 text-sm font-bold">
                4
              </div>
              <h4 className="font-semibold text-sm">Competitive Analysis</h4>
              <p className="text-xs text-gray-600 mt-1">
                Compare against reference sites and identify gaps
              </p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

/**
 * Simple CliftonStrengths Page
 * Follows the Content-Comparison pattern: URL input â†’ Scrape â†’ Analyze
 * No database dependencies, just direct AI analysis
 */

'use client';

import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
    BarChart3,
    Brain,
    CheckCircle,
    Lightbulb,
    Loader2,
    Target,
    TrendingUp,
    Users,
    Zap
} from 'lucide-react';
import { useState } from 'react';
import { ContentPreviewBox } from './ContentPreviewBox';

interface CliftonStrengthsData {
  overall_score: number;
  strategic_thinking_score: number;
  executing_score: number;
  influencing_score: number;
  relationship_building_score: number;
  dominant_domain: string;
  top_5_themes: Array<{
    theme_name: string;
    domain: string;
    score: number;
    evidence: string[];
    manifestation: string;
  }>;
  all_themes: Array<{
    theme_name: string;
    domain: string;
    score: number;
    evidence: string[];
  }>;
  recommendations: Array<{
    theme: string;
    action: string;
    impact: string;
  }>;
}

export function SimpleCliftonStrengthsPage() {
  const [url, setUrl] = useState('');
  const [isScraping, setIsScraping] = useState(false);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [scrapedContent, setScrapedContent] = useState<any>(null);
  const [result, setResult] = useState<CliftonStrengthsData | null>(null);
  const [error, setError] = useState<string | null>(null);

  const handleScrapeContent = async () => {
    if (!url.trim()) {
      setError('Please enter a website URL');
      return;
    }

    setIsScraping(true);
    setError(null);
    setScrapedContent(null);
    setResult(null);

    try {
      const response = await fetch('/api/scrape-content', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url }),
      });

      const data = await response.json();

      if (data.success) {
        setScrapedContent(data.data);
        console.log('âœ… Content scraped successfully');
      } else {
        throw new Error(data.error || 'Failed to scrape content');
      }
    } catch (error) {
      console.error('âŒ Content scraping failed:', error);
      setError(error instanceof Error ? error.message : 'Failed to scrape content');
    } finally {
      setIsScraping(false);
    }
  };

  const handleAnalyze = async () => {
    if (!scrapedContent) {
      setError('Please scrape content first');
      return;
    }

    setIsAnalyzing(true);
    setError(null);

    try {
      const response = await fetch('/api/analyze/clifton-strengths-standalone', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          url,
          scrapedContent
        }),
      });

      const data = await response.json();

      if (data.success) {
        setResult(data.data);
        console.log('âœ… CliftonStrengths analysis completed');
      } else {
        throw new Error(data.error || 'Analysis failed');
      }
    } catch (error) {
      console.error('âŒ CliftonStrengths analysis failed:', error);
      setError(error instanceof Error ? error.message : 'Analysis failed');
    } finally {
      setIsAnalyzing(false);
    }
  };

  const getDomainIcon = (domain: string) => {
    switch (domain) {
      case 'Strategic Thinking': return <Brain className="h-5 w-5 text-blue-600" />;
      case 'Executing': return <Zap className="h-5 w-5 text-green-600" />;
      case 'Influencing': return <TrendingUp className="h-5 w-5 text-purple-600" />;
      case 'Relationship Building': return <Users className="h-5 w-5 text-orange-600" />;
      default: return <BarChart3 className="h-5 w-5 text-gray-600" />;
    }
  };

  const getDomainColor = (domain: string) => {
    switch (domain) {
      case 'Strategic Thinking': return 'border-blue-200 bg-blue-50 dark:bg-blue-900/10';
      case 'Executing': return 'border-green-200 bg-green-50 dark:bg-green-900/10';
      case 'Influencing': return 'border-purple-200 bg-purple-50 dark:bg-purple-900/10';
      case 'Relationship Building': return 'border-orange-200 bg-orange-50 dark:bg-orange-900/10';
      default: return 'border-gray-200 bg-gray-50 dark:bg-gray-900/10';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 dark:from-slate-900 dark:to-slate-800">
      <div className="container mx-auto py-8 px-4 max-w-6xl">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-4">
            CliftonStrengths Analysis
          </h1>
          <p className="text-xl text-gray-600 dark:text-gray-300">
            Discover your organization's dominant strengths and cultural patterns
          </p>
        </div>

        {/* Input Form */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle className="flex items-center">
              <Target className="mr-2 h-6 w-6 text-blue-600" />
              Enter Website URL
            </CardTitle>
            <CardDescription>
              Enter your website URL to analyze organizational strengths using the CliftonStrengths framework
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div>
              <Label htmlFor="website-url">Website URL</Label>
              <Input
                id="website-url"
                name="website-url"
                type="url"
                placeholder="https://example.com"
                value={url}
                onChange={(e) => setUrl(e.target.value)}
                disabled={isScraping || isAnalyzing}
                aria-label="Enter website URL to analyze"
                aria-describedby="url-help"
                required
              />
              <p id="url-help" className="text-xs text-muted-foreground mt-1">
                Enter the URL of the website you want to analyze for organizational strengths
              </p>
            </div>

            <div className="flex gap-2">
              <Button
                onClick={handleScrapeContent}
                disabled={isScraping || !url.trim()}
                className="w-full"
              >
                {isScraping ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Scraping Website Content...
                  </>
                ) : (
                  <>
                    <Target className="mr-2 h-4 w-4" />
                    Step 1: Scrape Content
                  </>
                )}
              </Button>

              <Button
                onClick={handleAnalyze}
                disabled={isAnalyzing || !scrapedContent}
                className="w-full"
                variant="outline"
              >
                {isAnalyzing ? (
                  <>
                    <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                    Analyzing CliftonStrengths...
                  </>
                ) : (
                  <>
                    <Brain className="mr-2 h-4 w-4" />
                    Step 2: Analyze Strengths
                  </>
                )}
              </Button>
            </div>
          </CardContent>
        </Card>

        {/* Error Display */}
        {error && (
          <Card className="mb-8 border-red-200 bg-red-50 dark:bg-red-900/10">
            <CardContent className="pt-6">
              <p className="text-red-600 dark:text-red-400">{error}</p>
            </CardContent>
          </Card>
        )}

        {/* Scraped Content Preview */}
        {scrapedContent && (
          <ContentPreviewBox
            scrapedContent={scrapedContent}
            url={url}
            title="CliftonStrengths Analysis - Scraped Content Preview"
            description="Content successfully scraped from the website. Review the data before running CliftonStrengths analysis."
          />
        )}

        {/* Results */}
        {result && (
          <div className="space-y-8">
            {/* Overall Scores */}
            <Card className="border-green-200 bg-green-50 dark:bg-green-900/10">
              <CardHeader>
                <CardTitle className="flex items-center">
                  <CheckCircle className="mr-2 h-6 w-6 text-green-600" />
                  CliftonStrengths Analysis Results
                </CardTitle>
                <CardDescription>
                  Organizational strengths analysis for {url}
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
                  <div className="text-center p-4 border rounded-lg bg-white dark:bg-gray-800">
                    <p className="text-3xl font-bold text-green-600">{result.overall_score}</p>
                    <p className="text-sm text-gray-600 dark:text-gray-400">Overall Score</p>
                  </div>
                  <div className="text-center p-4 border rounded-lg bg-white dark:bg-gray-800">
                    <p className="text-3xl font-bold text-blue-600">{result.strategic_thinking_score}</p>
                    <p className="text-sm text-gray-600 dark:text-gray-400">Strategic Thinking</p>
                  </div>
                  <div className="text-center p-4 border rounded-lg bg-white dark:bg-gray-800">
                    <p className="text-3xl font-bold text-green-600">{result.executing_score}</p>
                    <p className="text-sm text-gray-600 dark:text-gray-400">Executing</p>
                  </div>
                  <div className="text-center p-4 border rounded-lg bg-white dark:bg-gray-800">
                    <p className="text-3xl font-bold text-purple-600">{result.influencing_score}</p>
                    <p className="text-sm text-gray-600 dark:text-gray-400">Influencing</p>
                  </div>
                </div>

                <div className="text-center p-4 border rounded-lg bg-white dark:bg-gray-800">
                  <p className="text-2xl font-bold text-orange-600">{result.relationship_building_score}</p>
                  <p className="text-sm text-gray-600 dark:text-gray-400">Relationship Building</p>
                </div>

                <div className="mt-4 text-center">
                  <Badge variant="outline" className="text-lg px-4 py-2">
                    Dominant Domain: {result.dominant_domain}
                  </Badge>
                </div>
              </CardContent>
            </Card>

            {/* Top 5 Themes */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center">
                  <Target className="mr-2 h-6 w-6 text-blue-600" />
                  Top 5 CliftonStrengths Themes
                </CardTitle>
                <CardDescription>
                  The organization's strongest themes based on content analysis
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {result.top_5_themes.map((theme, index) => (
                    <Card key={index} className={`border-l-4 ${getDomainColor(theme.domain)}`}>
                      <CardContent className="pt-4">
                        <div className="flex items-center justify-between mb-2">
                          <div className="flex items-center space-x-2">
                            {getDomainIcon(theme.domain)}
                            <h3 className="font-semibold text-lg">{theme.theme_name}</h3>
