                            {theme.evidence.map((evidence, idx) => (
                              <li key={idx} className="flex items-start">
                                <span className="mr-2">â€¢</span>
                                <span>{evidence}</span>
                              </li>
                            ))}
                          </ul>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>
              </CardContent>
            </Card>

            {/* Recommendations */}
            {result.recommendations && result.recommendations.length > 0 && (
              <Card>
