'use client';

import { useState, useEffect } from 'react';
import { CheckCircle, Circle, AlertCircle, Clock } from 'lucide-react';

interface SimpleStep {
  id: string;
  name: string;
  status: 'pending' | 'running' | 'completed' | 'failed' | 'skipped';
}

interface SimpleProgressTrackerProps {
  steps: SimpleStep[];
  currentStep?: string | undefined;
}

export default function SimpleProgressTracker({ steps, currentStep }: SimpleProgressTrackerProps) {
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    const completed = steps.filter(step => step.status === 'completed').length;
    const total = steps.length;
    setProgress(Math.round((completed / total) * 100));
  }, [steps]);

  const getStepIcon = (step: SimpleStep) => {
    switch (step.status) {
      case 'completed':
        return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'running':
        return <Clock className="h-5 w-5 text-blue-500 animate-spin" />;
      case 'failed':
        return <AlertCircle className="h-5 w-5 text-red-500" />;
      case 'skipped':
        return <Circle className="h-5 w-5 text-gray-400" />;
      default:
        return <Circle className="h-5 w-5 text-gray-400" />;
    }
  };

  const getStepTextColor = (step: SimpleStep) => {
    switch (step.status) {
      case 'completed':
        return 'text-green-600';
      case 'running':
        return 'text-blue-600';
      case 'failed':
        return 'text-red-600';
      case 'skipped':
        return 'text-gray-500';
      default:
        return 'text-gray-500';
    }
  };

  return (
    <div className="w-full max-w-md mx-auto">
      {/* Progress Bar */}
      <div className="mb-6">
        <div className="flex justify-between text-sm text-gray-600 mb-2">
          <span>Progress</span>
          <span>{progress}%</span>
        </div>
        <div className="w-full bg-gray-200 rounded-full h-2">
          <div 
            className="bg-gradient-to-r from-blue-500 to-green-500 h-2 rounded-full transition-all duration-300"
            style={{ width: `${progress}%` }}
          />
        </div>
      </div>

      {/* Steps List */}
      <div className="space-y-3">
        {steps.map((step, index) => (
          <div key={step.id} className="flex items-center space-x-3">
            {getStepIcon(step)}
            <span className={`text-sm font-medium ${getStepTextColor(step)}`}>
              {step.name}
            </span>
            {step.status === 'completed' && (
              <span className="text-xs text-green-600 ml-auto">âœ“ Done</span>
            )}
            {step.status === 'running' && (
              <span className="text-xs text-blue-600 ml-auto">Running...</span>
            )}
            {step.status === 'failed' && (
              <span className="text-xs text-red-600 ml-auto">Failed</span>
            )}
          </div>
        ))}
      </div>

      {/* Summary */}
      <div className="mt-4 p-3 bg-gray-50 rounded-lg">
        <div className="text-sm text-gray-600">
          <span className="font-medium">{steps.filter(s => s.status === 'completed').length}</span> of{' '}
          <span className="font-medium">{steps.length}</span> steps completed
        </div>
      </div>
    </div>
  );
}

/**
 * Standalone B2B Elements of Value Analysis Page
 * Focuses on enterprise value elements and revenue opportunities
 */

'use client';

import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { BarChart3, DollarSign, Download, Loader2, Target, TrendingUp } from 'lucide-react';
import { useState } from 'react';
import { ContentPreviewBox } from './ContentPreviewBox';
import { MarkdownReportGenerator } from '@/lib/markdown-report-generator';

interface B2BValueElement {
  element: string;
  current_strength: number;
  revenue_potential: string;
  implementation_effort: string;
  estimated_roi: string;
  target_audience: string;
}

interface B2BRecommendation {
  priority: 'High' | 'Medium' | 'Low';
  action: string;
  expected_revenue_impact: string;
  implementation_cost: string;
  timeline: string;
  roi_estimate: string;
}

interface B2BAnalysisData {
  overall_score: number;
  table_stakes_score: number;
  functional_score: number;
  ease_of_doing_business_score: number;
  individual_score: number;
  inspirational_score: number;
  revenue_opportunities: B2BValueElement[];
  recommendations: B2BRecommendation[];
}

export function StandaloneElementsOfValueB2BPage() {
  const [url, setUrl] = useState('');
  const [isScraping, setIsScraping] = useState(false);
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [scrapedContent, setScrapedContent] = useState<any>(null);
  const [result, setResult] = useState<B2BAnalysisData | null>(null);
  const [error, setError] = useState<string | null>(null);

  const handleScrapeContent = async () => {
    if (!url.trim()) {
      setError('Please enter a website URL');
      return;
    }

    setIsScraping(true);
    setError(null);
    setScrapedContent(null);
    setResult(null);

    try {
      const response = await fetch('/api/scrape-content', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ url: url.trim() }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Content scraping failed');
      }

      if (data.success) {
        setScrapedContent(data.data);
      } else {
        throw new Error(data.error || 'Content scraping failed');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Content scraping failed');
    } finally {
      setIsScraping(false);
    }
  };

  const handleAnalyze = async () => {
    if (!scrapedContent) {
      setError('Please scrape content first');
      return;
    }

    setIsAnalyzing(true);
    setError(null);
    setResult(null);

    try {
      const response = await fetch('/api/analyze/elements-value-b2b-standalone', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          url: url.trim(),
          scrapedContent: scrapedContent
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Analysis failed');
      }

      if (data.success) {
        setResult(data.data);
      } else {
        throw new Error(data.error || 'Analysis failed');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Analysis failed');
    } finally {
      setIsAnalyzing(false);
    }
  };

  const getEffortBadgeColor = (effort: string) => {
    switch (effort.toLowerCase()) {
      case 'low': return 'bg-green-500';
      case 'medium': return 'bg-yellow-500';
      case 'high': return 'bg-red-500';
      default: return 'bg-gray-500';
    }
  };

  const getPriorityBadgeColor = (priority: string) => {
    switch (priority.toLowerCase()) {
      case 'high': return 'bg-red-500';
      case 'medium': return 'bg-yellow-500';
      case 'low': return 'bg-green-500';
      default: return 'bg-gray-500';
    }
  };

  const downloadMarkdown = () => {
    if (!result) return;

    const markdown = MarkdownReportGenerator.generateB2BReport(result, url);
    const blob = new Blob([markdown], { type: 'text/markdown' });
    const downloadUrl = URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = downloadUrl;
    a.download = `b2b-elements-analysis-${url.replace(/[^a-zA-Z0-9]/g, '-')}-${Date.now()}.md`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
    URL.revokeObjectURL(downloadUrl);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 dark:from-slate-900 dark:to-slate-800">
      <div className="container mx-auto py-8 px-4">
        {/* Header */}
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-gray-900 dark:text-white mb-4">
            B2B Elements of Value Analysis
          </h1>
          <p className="text-xl text-gray-600 dark:text-gray-300 max-w-3xl mx-auto">
