interface AnalysisNotificationProps {
  type: 'error' | 'success' | 'info';
  title: string;
  message: string;
  details?: string;
  action?: {
    label: string;
    onClick: () => void;
  };
  onDismiss?: () => void;
}

export function AnalysisNotification({ 
  type, 
  title, 
  message, 
  details, 
  action, 
  onDismiss 
}: AnalysisNotificationProps) {
  const [isVisible, setIsVisible] = useState(true);

  const handleDismiss = () => {
    setIsVisible(false);
    onDismiss?.();
  };

  if (!isVisible) return null;

  const getIcon = () => {
    switch (type) {
      case 'error':
        return <AlertCircle className="h-5 w-5 text-red-500" />;
      case 'success':
        return <CheckCircle className="h-5 w-5 text-green-500" />;
      case 'info':
        return <AlertCircle className="h-5 w-5 text-blue-500" />;
    }
  };

  const getCardClass = () => {
    switch (type) {
      case 'error':
        return 'border-red-200 bg-red-50';
      case 'success':
        return 'border-green-200 bg-green-50';
      case 'info':
        return 'border-blue-200 bg-blue-50';
    }
  };

  const getTextClass = () => {
    switch (type) {
      case 'error':
        return 'text-red-800';
      case 'success':
        return 'text-green-800';
      case 'info':
        return 'text-blue-800';
    }
  };

  return (
    <Card className={`border-2 ${getCardClass()}`}>
      <CardContent className="p-4">
        <div className="flex items-start gap-3">
          {getIcon()}
          <div className="flex-1">
            <h4 className={`font-semibold ${getTextClass()}`}>{title}</h4>
            <p className={`text-sm ${getTextClass()} opacity-80 mt-1`}>{message}</p>
            {details && (
              <p className={`text-xs ${getTextClass()} opacity-60 mt-2`}>{details}</p>
            )}
            {action && (
              <div className="mt-3">
                <Button
                  size="sm"
                  variant={type === 'error' ? 'destructive' : 'default'}
                  onClick={action.onClick}
                  className="text-xs"
                >
                  {action.label}
                </Button>
              </div>
            )}
          </div>
          {onDismiss && (
            <Button
              variant="ghost"
              size="sm"
              onClick={handleDismiss}
              className="text-gray-400 hover:text-gray-600"
            >
              Ã—
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

interface SiteIdentificationProps {
  url: string;
  isAnalyzing?: boolean;
}

export function SiteIdentification({ url, isAnalyzing = false }: SiteIdentificationProps) {
  const [siteInfo, setSiteInfo] = useState<{
    title: string;
    favicon: string;
    domain: string;
  } | null>(null);

  useEffect(() => {
    if (url) {
      try {
        const urlObj = new URL(url);
        const domain = urlObj.hostname;

        setSiteInfo({
          title: domain.replace('www.', ''),
          favicon: `https://www.google.com/s2/favicons?domain=${domain}`,
          domain: domain
        });
      } catch (error) {
        setSiteInfo({
          title: url,
          favicon: '',
          domain: url
        });
      }
    }
  }, [url]);

  if (!siteInfo) return null;

  return (
    <Card className="border-2 border-gray-200">
      <CardContent className="p-4">
        <div className="flex items-center gap-3">
          {siteInfo.favicon && (
            <Image 
              src={siteInfo.favicon} 
              alt={`${siteInfo.domain} favicon`}
              width={24}
              height={24}
              className="rounded-sm w-6 h-6"
              style={{ width: 'auto', height: 'auto' }}
              onError={(e) => {
                e.currentTarget.style.display = 'none';
              }}
            />
          )}
          <div className="flex-1">
            <h3 className="font-semibold text-gray-900">{siteInfo.title}</h3>
            <p className="text-sm text-gray-600">{siteInfo.domain}</p>
          </div>
          <div className="flex items-center gap-2">
            <Badge variant="outline" className="text-xs">
              {isAnalyzing ? 'Analyzing...' : 'Ready'}
            </Badge>
            <Button
              variant="ghost"
              size="sm"
              asChild
              className="text-gray-400 hover:text-gray-600"
            >
              <a href={url} target="_blank" rel="noopener noreferrer">
                <ExternalLink className="h-4 w-4" />
              </a>
            </Button>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

'use client';

import { User } from '@/lib/auth';
import React, { createContext, useContext, useEffect, useState } from 'react';

interface AuthContextType {
  user: User | null;
  loading: boolean;
  signIn: (email: string, password: string) => Promise<boolean>;
  signUp: (email: string, password: string, name: string) => Promise<boolean>;
  signOut: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: React.ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Check for existing auth token on mount
    checkAuth();
  }, []);

  const checkAuth = async () => {
    try {
      // Check for JWT token in localStorage
      const token = localStorage.getItem('auth_token');
      if (token) {
        // Verify token with API
        const response = await fetch('/api/auth/me', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (response.ok) {
          const userData = await response.json();
          setUser(userData.user);
        } else {
          // Invalid token, clear it
          localStorage.removeItem('auth_token');
        }
      }
    } catch (error) {
      console.error('Auth check failed:', error);
      localStorage.removeItem('auth_token');
    } finally {
      setLoading(false);
    }
  };

  const signIn = async (email: string, password: string): Promise<boolean> => {
    try {
      // Real authentication with API
      const response = await fetch('/api/auth/signin', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email, password })
      });

      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        // Store JWT token
        localStorage.setItem('auth_token', data.token);
        return true;
      }
      return false;
    } catch (error) {
      console.error('Signin error:', error);
      return false;
    }
  };

  const signUp = async (
    email: string,
    password: string,
    name: string
  ): Promise<boolean> => {
    try {
      // Real signup with API
      const response = await fetch('/api/auth/signup', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ email, password, name })
      });

      if (response.ok) {
        const data = await response.json();
        setUser(data.user);
        // Store JWT token
        localStorage.setItem('auth_token', data.token);
        return true;
      }
      return false;
    } catch (error) {
      console.error('Signup error:', error);
      return false;
    }
  };

  const signOut = async () => {
    try {
      // Clear token and user
      localStorage.removeItem('auth_token');
      setUser(null);
    } catch (error) {
      console.error('Signout error:', error);
    }
  };

  const value: AuthContextType = {
    user,
    loading,
    signIn,
    signUp,
    signOut,
  };

  return <AuthContext.Provider value={value}>{children}</AuthContext.Provider>;
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

/**
 * Robust state management hook with error handling and loading states
 */

import { useState, useCallback, useRef, useEffect } from 'react';

interface RobustState<T> {
  data: T | null;
  loading: boolean;
  error: string | null;
  success: boolean;
}

interface RobustStateActions<T> {
  setData: (data: T) => void;
  setLoading: (loading: boolean) => void;
  setError: (error: string | null) => void;
  reset: () => void;
  execute: (asyncFn: () => Promise<T>) => Promise<void>;
}

export function useRobustState<T>(
  initialData: T | null = null
): [RobustState<T>, RobustStateActions<T>] {
  const [state, setState] = useState<RobustState<T>>({
    data: initialData,
    loading: false,
    error: null,
    success: false,
  });

  const isMountedRef = useRef(true);

  useEffect(() => {
    return () => {
      isMountedRef.current = false;
    };
  }, []);

  const safeSetState = useCallback((updater: Partial<RobustState<T>>) => {
    if (isMountedRef.current) {
      setState(prev => ({ ...prev, ...updater }));
    }
  }, []);

  const setData = useCallback((data: T) => {
    safeSetState({
      data,
      loading: false,
      error: null,
      success: true,
    });
  }, [safeSetState]);

  const setLoading = useCallback((loading: boolean) => {
    safeSetState({ loading });
  }, [safeSetState]);

  const setError = useCallback((error: string | null) => {
    safeSetState({
      error,
      loading: false,
      success: false,
    });
  }, [safeSetState]);

  const reset = useCallback(() => {
    safeSetState({
      data: initialData,
      loading: false,
      error: null,
      success: false,
    });
  }, [safeSetState, initialData]);

  const execute = useCallback(async (asyncFn: () => Promise<T>) => {
    setLoading(true);
    setError(null);

    try {
      const result = await asyncFn();
      setData(result);
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'An unexpected error occurred';
      setError(errorMessage);
      console.error('RobustState execution error:', error);
    }
  }, [setLoading, setError, setData]);

  const actions: RobustStateActions<T> = {
    setData,
    setLoading,
    setError,
    reset,
    execute,
  };

  return [state, actions];
}

/**
 * Hook for managing API calls with retry logic
 */
export function useApiCall<T>() {
  const [state, actions] = useRobustState<T>();

  const callApi = useCallback(async (
    apiCall: () => Promise<T>,
    options: {
      retries?: number;
      retryDelay?: number;
      onRetry?: (attempt: number) => void;
    } = {}
  ) => {
    const { retries = 3, retryDelay = 1000, onRetry } = options;
    let lastError: Error;

    for (let attempt = 1; attempt <= retries; attempt++) {
      try {
        const result = await apiCall();
        actions.setData(result);
        return result;
      } catch (error) {
        lastError = error as Error;

        if (attempt < retries) {
          onRetry?.(attempt);
          await new Promise(resolve => setTimeout(resolve, retryDelay * attempt));
        }
      }
    }

    actions.setError(lastError!.message);
    throw lastError!;
  }, [actions]);

  return {
    ...state,
    callApi,
    reset: actions.reset,
  };
}

'use client';

import { useState, useEffect } from 'react';

interface SimpleStep {
  id: string;
  name: string;
  status: 'pending' | 'running' | 'completed' | 'failed';
}

interface SimpleAnalysis {
  id: string;
  url: string;
  status: 'running' | 'completed' | 'failed';
  progress: number;
  steps: {
    baseAnalysis: boolean;
    pageAudit: boolean;
    lighthouse: boolean;
    geminiInsights: boolean;
  };
  startedAt: string;
  completedAt?: string;
}

export function useSimpleProgress() {
  const [analyses, setAnalyses] = useState<SimpleAnalysis[]>([]);

  useEffect(() => {
    // Load from localStorage
    try {
      const saved = localStorage.getItem('simple-analyses');
      if (saved) {
        setAnalyses(JSON.parse(saved));
      }
    } catch (error) {
      console.warn('Failed to load analyses:', error);
    }
  }, []);

  const saveAnalyses = (newAnalyses: SimpleAnalysis[]) => {
    setAnalyses(newAnalyses);
    try {
      localStorage.setItem('simple-analyses', JSON.stringify(newAnalyses));
    } catch (error) {
      console.warn('Failed to save analyses:', error);
    }
  };

  const startAnalysis = (url: string): string => {
    const id = `analysis_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
    const newAnalysis: SimpleAnalysis = {
      id,
      url,
      status: 'running',
      progress: 0,
      steps: {
        baseAnalysis: false,
        pageAudit: false,
        lighthouse: false,
        geminiInsights: false,
      },
      startedAt: new Date().toISOString(),
    };

    const updatedAnalyses = [newAnalysis, ...analyses];
    saveAnalyses(updatedAnalyses);
    return id;
  };

  const updateStep = (analysisId: string, step: keyof SimpleAnalysis['steps'], completed: boolean) => {
    const updatedAnalyses = analyses.map(analysis => {
      if (analysis.id === analysisId) {
        const updatedSteps = { ...analysis.steps, [step]: completed };
        const completedSteps = Object.values(updatedSteps).filter(Boolean).length;
        const progress = Math.round((completedSteps / 4) * 100);

        const status: 'running' | 'completed' | 'failed' = progress === 100 ? 'completed' : 'running';
        const completedAt = status === 'completed' ? new Date().toISOString() : undefined;

        return {
          ...analysis,
          steps: updatedSteps,
          progress,
          status,
          ...(completedAt && { completedAt }),
        };
      }
      return analysis;
    });

    saveAnalyses(updatedAnalyses);
  };

  const markFailed = (analysisId: string) => {
    const updatedAnalyses = analyses.map(analysis => {
      if (analysis.id === analysisId) {
        return {
          ...analysis,
          status: 'failed' as const,
          completedAt: new Date().toISOString(),
        };
      }
      return analysis;
    });

    saveAnalyses(updatedAnalyses);
  };

  const deleteAnalysis = (analysisId: string) => {
    const updatedAnalyses = analyses.filter(analysis => analysis.id !== analysisId);
    saveAnalyses(updatedAnalyses);
  };

  const clearAll = () => {
    saveAnalyses([]);
  };

  return {
    analyses,
    startAnalysis,
    updateStep,
    markFailed,
    deleteAnalysis,
    clearAll,
  };
}

