/**
 * Enhanced Controlled Analysis System
 * Optimized for thorough analysis with clear progress tracking and actionable deliverables
 */

import { GoogleGenerativeAI } from '@google/generative-ai';
import { OptimizedContentCollector, ComprehensiveContentData } from './optimized-content-collector';
import { reportStorage } from './report-storage';
import { apiKeyManager } from './secure-api-keys';

export interface AnalysisProgress {
  stepId: string;
  stepName: string;
  status: 'pending' | 'running' | 'completed' | 'failed';
  progress: number; // 0-100
  startTime?: Date;
  endTime?: Date;
  estimatedDuration?: number;
  result?: any;
  error?: string;
  details?: string;
}

export interface EnhancedAnalysisConfig {
  url: string;
  onProgressUpdate: (progress: AnalysisProgress) => void;
  enableDetailedLogging: boolean;
  timeoutPerStep: number;
}

export interface ActionableReport {
  executiveSummary: {
    overallScore: number;
    rating: string;
    keyStrengths: string[];
    criticalWeaknesses: string[];
    priorityActions: string[];
  };
  detailedAnalysis: {
    goldenCircle: any;
    elementsOfValue: any;
    b2bElements: any;
    cliftonStrengths: any;
    technicalPerformance: any;
    contentQuality: any;
  };
  actionableRecommendations: {
    immediateActions: { action: string; impact: string; effort: string; timeline: string; }[];
    quickWins: { action: string; impact: string; effort: string; timeline: string; }[];
    strategicInitiatives: { action: string; impact: string; effort: string; timeline: string; }[];
  };
  implementationRoadmap: {
    phase1: { timeframe: string; actions: string[]; successMetrics: string[]; }[];
    phase2: { timeframe: string; actions: string[]; successMetrics: string[]; }[];
    phase3: { timeframe: string; actions: string[]; successMetrics: string[]; }[];
  };
}

export class EnhancedControlledAnalyzer {
  private genAI: GoogleGenerativeAI;
  private config: EnhancedAnalysisConfig;
  private contentData: ComprehensiveContentData | null = null;
  private analysisResults: any = {};

  constructor(config: EnhancedAnalysisConfig) {
    // Security check - ensure this runs server-side only
    apiKeyManager.validateServerSideOnly();

    this.config = config;

    // Use secure API key management
    const geminiConfig = apiKeyManager.getGeminiApiKey();
    if (!geminiConfig.isConfigured) {
      throw new Error('GEMINI_API_KEY not configured. Please set GEMINI_API_KEY in environment variables.');
    }

    this.genAI = new GoogleGenerativeAI(geminiConfig.key);
  }

  /**
   * Execute enhanced controlled analysis with optimized content collection
   */
  async execute(): Promise<ActionableReport> {
    console.log(`ðŸš€ Starting enhanced controlled analysis for: ${this.config.url}`);

    const startTime = Date.now();

    try {
      // Step 1: Comprehensive Content Collection (PRIMARY STEP)
      await this.executeStep('content_collection', 'Comprehensive Content Collection', async () => {
        const collector = new OptimizedContentCollector(this.config.url, (step, progress) => {
          this.updateProgress('content_collection', 'Comprehensive Content Collection', 'running', progress, `Collecting: ${step}`);
        });

        this.contentData = await collector.collectComprehensiveContent();
        return this.contentData;
      });

      // Step 2: Golden Circle Analysis (using comprehensive content)
      await this.executeStep('golden_circle', 'Golden Circle Analysis', async () => {
        return await this.analyzeGoldenCircle();
      });

      // Step 3: Elements of Value Analysis
      await this.executeStep('elements_of_value', 'B2C Elements of Value Analysis', async () => {
        return await this.analyzeElementsOfValue();
      });

      // Step 4: B2B Elements Analysis
      await this.executeStep('b2b_elements', 'B2B Elements of Value Analysis', async () => {
        return await this.analyzeB2BElements();
      });

      // Step 5: CliftonStrengths Analysis
      await this.executeStep('clifton_strengths', 'CliftonStrengths Analysis', async () => {
        return await this.analyzeCliftonStrengths();
      });

      // Step 6: Google SEO Tools Analysis (Search Console, Keyword Planner, Google Trends)
      await this.executeStep('google_seo_tools', 'Google SEO Tools Analysis', async () => {
        return await this.analyzeGoogleSEOTools();
      });

      // Step 7: Technical Performance Analysis
      await this.executeStep('technical_performance', 'Technical Performance Analysis', async () => {
        return await this.analyzeTechnicalPerformance();
      });

      // Step 8: Content Quality Analysis
      await this.executeStep('content_quality', 'Content Quality Analysis', async () => {
        return await this.analyzeContentQuality();
      });

      // Step 9: Generate Actionable Report
      await this.executeStep('actionable_report', 'Generating Actionable Report', async () => {
        return await this.generateActionableReport();
      });

      const totalDuration = Date.now() - startTime;
      console.log(`âœ… Enhanced analysis completed in ${totalDuration}ms`);

      // Store the report
      const report = this.analysisResults.actionable_report;
      await reportStorage.storeReport(report, this.config.url, 'enhanced-analysis');

      return report;

    } catch (error) {
      console.error('Enhanced analysis failed:', error);
      throw error;
    }
  }

  /**
   * Execute individual analysis step with enhanced progress tracking
   */
  private async executeStep(
    stepId: string, 
    stepName: string, 
    stepFunction: () => Promise<any>
  ): Promise<void> {
    const startTime = new Date();

    this.updateProgress(stepId, stepName, 'running', 0, 'Starting analysis...');

    try {
      // Execute step with timeout
      const result = await Promise.race([
        stepFunction(),
        new Promise((_, reject) => 
          setTimeout(() => reject(new Error(`Step timeout after ${this.config.timeoutPerStep}ms`)), this.config.timeoutPerStep)
        )
      ]);

      const endTime = new Date();
      const duration = endTime.getTime() - startTime.getTime();

      this.analysisResults[stepId] = result;

      this.updateProgress(stepId, stepName, 'completed', 100, 
        `Completed in ${duration}ms`, endTime);

      if (this.config.enableDetailedLogging) {
        console.log(`âœ… ${stepName} completed:`, {
          duration: `${duration}ms`,
          resultSize: JSON.stringify(result).length,
          timestamp: endTime.toISOString()
        });
      }

    } catch (error) {
      const endTime = new Date();

      this.updateProgress(stepId, stepName, 'failed', 0, 
        `Failed: ${error instanceof Error ? error.message : 'Unknown error'}`, endTime);

      throw error;
    }
  }

  /**
   * Update progress and notify callback
   */
  private updateProgress(
    stepId: string, 
    stepName: string, 
    status: AnalysisProgress['status'], 
    progress: number, 
    details?: string,
    endTime?: Date
  ): void {
    const progressData: AnalysisProgress = {
      stepId,
      stepName,
      status,
      progress,
      ...(status === 'running' && { startTime: new Date() }),
      ...(endTime && { endTime }),
      ...(details && { details })
    };

    this.config.onProgressUpdate(progressData);
  }

