  /**
   * Extract keywords from comprehensive content data
   */
  private extractKeywordsFromContent(contentData: ComprehensiveContentData): string[] {
    const keywords: string[] = [];

    // Extract from title and meta description
    if (contentData.title) {
      const titleWords = contentData.title.toLowerCase()
        .replace(/[^\w\s]/g, ' ')
        .split(/\s+/)
        .filter(word => word.length > 3);
      keywords.push(...titleWords);
    }

    if (contentData.metaDescription) {
      const descWords = contentData.metaDescription.toLowerCase()
        .replace(/[^\w\s]/g, ' ')
        .split(/\s+/)
        .filter(word => word.length > 3);
      keywords.push(...descWords);
    }

    // Extract from content (first 1000 words)
    if (contentData.content) {
      const contentWords = contentData.content.toLowerCase()
        .replace(/[^\w\s]/g, ' ')
        .split(/\s+/)
        .slice(0, 1000)
        .filter(word => word.length > 4);
      keywords.push(...contentWords);
    }

    // Extract from headings
    contentData.seoData.headings.forEach(heading => {
      const headingWords = heading.text.toLowerCase()
        .replace(/[^\w\s]/g, ' ')
        .split(/\s+/)
        .filter(word => word.length > 3);
      keywords.push(...headingWords);
    });

    // Remove duplicates and return top keywords
    const uniqueKeywords = [...new Set(keywords)];
    return uniqueKeywords.slice(0, 25); // Top 25 keywords
  }

