        metaTags: this.contentData.seoData.metaTags.length,
        headings: this.contentData.seoData.headings,
        canonicalUrl: this.contentData.seoData.canonicalUrl,
        robotsMeta: this.contentData.seoData.robotsMeta
      },
      accessibility: {
        imagesWithAltText: this.contentData.accessibilityData.altTexts.filter(img => img.alt).length,
        totalImages: this.contentData.accessibilityData.altTexts.length,
        linksWithTitles: this.contentData.accessibilityData.links.filter(link => link.hasTitle).length,
        totalLinks: this.contentData.accessibilityData.links.length
      }
    };
  }

  /**
   * Content Quality Analysis
   */
  private async analyzeContentQuality(): Promise<any> {
    if (!this.contentData) throw new Error('Content data not available');

    const prompt = `
    Analyze the content quality with EXACT EVIDENCE from the website:

    CONTENT: ${this.contentData.content.substring(0, 4000)}

    Evaluate:
    1. Content depth and comprehensiveness
    2. Clarity and readability
    3. Value proposition strength
    4. Call-to-action effectiveness
    5. User experience quality
    6. Information architecture
    7. Engagement factors

    For each area:
    - Score: 1-10
    - Evidence: Specific examples from content
    - Recommendations: Specific improvements

    Return JSON format with detailed analysis and specific recommendations.
    `;

    return await this.callGeminiAPI(prompt);
  }

  /**
   * Generate comprehensive actionable report
   */
  private async generateActionableReport(): Promise<ActionableReport> {
    const goldenCircle = this.analysisResults.golden_circle;
    const elementsOfValue = this.analysisResults.elements_of_value;
    const b2bElements = this.analysisResults.b2b_elements;
    const cliftonStrengths = this.analysisResults.clifton_strengths;
    const technicalPerformance = this.analysisResults.technical_performance;
    const contentQuality = this.analysisResults.content_quality;

    // Calculate overall score
    const overallScore = this.calculateOverallScore(goldenCircle, elementsOfValue, b2bElements, cliftonStrengths);

    // Extract key insights
    const keyStrengths = this.extractKeyStrengths(goldenCircle, elementsOfValue, b2bElements, cliftonStrengths);
    const criticalWeaknesses = this.extractCriticalWeaknesses(goldenCircle, elementsOfValue, b2bElements, cliftonStrengths);
    const priorityActions = this.generatePriorityActions(goldenCircle, elementsOfValue, b2bElements, cliftonStrengths);

    return {
      executiveSummary: {
        overallScore,
        rating: this.getRating(overallScore),
        keyStrengths,
        criticalWeaknesses,
        priorityActions
      },
      detailedAnalysis: {
        goldenCircle,
        elementsOfValue,
        b2bElements,
        cliftonStrengths,
        technicalPerformance,
        contentQuality
      },
      actionableRecommendations: {
        immediateActions: this.generateImmediateActions(goldenCircle, contentQuality),
        quickWins: this.generateQuickWins(elementsOfValue, technicalPerformance),
        strategicInitiatives: this.generateStrategicInitiatives(b2bElements, cliftonStrengths)
      },
      implementationRoadmap: {
        phase1: this.generatePhase1Roadmap(priorityActions),
        phase2: this.generatePhase2Roadmap(priorityActions),
        phase3: this.generatePhase3Roadmap(priorityActions)
      }
    };
  }

  /**
   * Call Gemini API with error handling
   */
  private async callGeminiAPI(prompt: string): Promise<any> {
    try {
      const model = this.genAI.getGenerativeModel({ model: 'gemini-2.5-flash' });
      const result = await model.generateContent(prompt);
      const response = await result.response;
      const text = response.text();

      // Parse JSON response
      let jsonText = text.trim();
      if (jsonText.startsWith('```json')) {
        jsonText = jsonText.replace(/^```json\s*/, '').replace(/\s*```$/, '');
      } else if (jsonText.startsWith('```')) {
        jsonText = jsonText.replace(/^```\s*/, '').replace(/\s*```$/, '');
      }

      return JSON.parse(jsonText);
    } catch (error) {
      console.error('Gemini API call failed:', error);
      throw new Error(`AI analysis failed: ${error instanceof Error ? error.message : 'Unknown error'}`);
    }
  }

  // Helper methods for report generation
  private calculateOverallScore(goldenCircle: any, elementsOfValue: any, b2bElements: any, cliftonStrengths: any): number {
    const scores = [
      goldenCircle?.overallScore || 0,
      elementsOfValue?.overallScore || 0,
      b2bElements?.overallScore || 0,
      cliftonStrengths?.overallScore || 0
    ];
    return Math.round(scores.reduce((sum, score) => sum + score, 0) / scores.length);
  }

  private getRating(score: number): string {
    if (score >= 90) return 'Excellent';
    if (score >= 80) return 'Very Good';
    if (score >= 70) return 'Good';
    if (score >= 60) return 'Fair';
    if (score >= 50) return 'Needs Improvement';
    return 'Critical';
  }

  private extractKeyStrengths(goldenCircle: any, elementsOfValue: any, b2bElements: any, cliftonStrengths: any): string[] {
    const strengths = [];
    if (goldenCircle?.overallScore > 70) strengths.push('Strong Golden Circle clarity');
    if (elementsOfValue?.overallScore > 70) strengths.push('Strong Elements of Value alignment');
    if (b2bElements?.overallScore > 70) strengths.push('Strong B2B value proposition');
    if (cliftonStrengths?.overallScore > 70) strengths.push('Strong CliftonStrengths alignment');
    return strengths;
  }

  private extractCriticalWeaknesses(goldenCircle: any, elementsOfValue: any, b2bElements: any, cliftonStrengths: any): string[] {
    const weaknesses = [];
    if (goldenCircle?.overallScore < 50) weaknesses.push('Weak Golden Circle clarity');
    if (elementsOfValue?.overallScore < 50) weaknesses.push('Weak Elements of Value alignment');
    if (b2bElements?.overallScore < 50) weaknesses.push('Weak B2B value proposition');
    if (cliftonStrengths?.overallScore < 50) weaknesses.push('Weak CliftonStrengths alignment');
    return weaknesses;
  }

  private generatePriorityActions(goldenCircle: any, elementsOfValue: any, b2bElements: any, cliftonStrengths: any): string[] {
    const actions = [];
    if (goldenCircle?.overallScore < 60) actions.push('Strengthen Golden Circle messaging');
