      // Fallback to Claude if Gemini fails and Claude key is available
      if (process.env.CLAUDE_API_KEY && process.env.CLAUDE_API_KEY !== 'your-real-key-here') {
        analysisResult = await analyzeWithClaude(content, analysisType);
