    // Step 3: Run Lighthouse analysis
    console.log('Running Lighthouse performance analysis...');
    const lighthouseAnalysis = await runLighthouseAnalysis(url);

    // Step 4: Format result
    const result: WebsiteAnalysisResult = {
      id: generateId(),
