  static async analyze(
    analysisId: string,
    content: any,
    industry?: string,
    patterns?: PatternMatch[]
  ): Promise<GoldenCircleAnalysis> {
    // Get patterns if not provided
    if (!patterns) {
      patterns = await SimpleSynonymDetectionService.findValuePatterns(
        content.text || content.content,
